(4702, '', '', NULL, NULL, 27, 12, 12, 4702, 47, 522, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'وَالسَّمَآءَ بَنَيْنَٰهَا بِأَيَيْدٖۖ وَإِنَّا لَمُوسِعُونَۖ ﰮ', '', NULL),
(4703, '', '', NULL, NULL, 27, 13, 12, 4703, 48, 522, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'وَالَارْضَ فَرَشْنَٰهَاۖ فَنِعْمَ اَ۬لْمَٰهِدُونَۖ ﰯ', '', NULL),
(4704, '', '', NULL, NULL, 27, 14, 13, 4704, 49, 522, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'وَمِن كُلِّ شَےْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَّكَّرُونَۖ ﰰ', '', NULL),
(4705, '', '', NULL, NULL, 27, 14, 14, 4705, 50, 522, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'فَفِرُّوٓاْ إِلَى اَ۬للَّهِ إِنِّے لَكُم مِّنْهُ نَذِيرٞ مُّبِينٞۖ ﰱ', '', NULL),
(4706, '', '', NULL, NULL, 27, 15, 15, 4706, 51, 522, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'وَلَا تَجْعَلُواْ مَعَ اَ۬للَّهِ إِلَٰهاً اٰخَرَ إِنِّے لَكُم مِّنْهُ نَذِيرٞ مُّبِينٞۖ ﰲ', '', NULL),
(4707, '', '', NULL, NULL, 27, 2, 1, 4707, 52, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'كَذَٰلِكَۖ مَآ أَتَى اَ۬لذِينَ مِن قَبْلِهِم مِّن رَّسُولٍ اِلَّا قَالُواْ سَاحِرٌ اَوْ مَجْنُونٌۖ ﰳ', '', NULL),
(4708, '', '', NULL, NULL, 27, 2, 2, 4708, 53, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'اَتَوَاصَوْاْ بِهِۦۖ بَلْ هُمْ قَوْمٞ طَاغُونَۖ ﰴ', '', NULL),
(4709, '', '', NULL, NULL, 27, 3, 2, 4709, 54, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'فَتَوَلَّ عَنْهُمْ فَمَآ أَنتَ بِمَلُومٖ ﰵ', '', NULL),
(4710, '', '', NULL, NULL, 27, 3, 3, 4710, 55, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'وَذَكِّرْۖ فَإِنَّ اَ۬لذِّكْر۪ىٰ تَنفَعُ اُ۬لْمُومِنِينَۖ ﰶ', '', NULL),
(4711, '', '', NULL, NULL, 27, 4, 4, 4711, 56, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, '۞ وَمَا خَلَقْتُ اُ۬لْجِنَّ وَالِانسَ إِلَّا لِيَعْبُدُونِۖ ﰷ', '', NULL),
(4712, '', '', NULL, NULL, 27, 5, 4, 4712, 57, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'مَآ أُرِيدُ مِنْهُم مِّن رِّزْقٖ وَمَآ أُرِيدُ أَنْ يُّطْعِمُونِۖ ﰸ', '', NULL),
(4713, '', '', NULL, NULL, 27, 6, 5, 4713, 58, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'إِنَّ اَ۬للَّهَ هُوَ اَ۬لرَّزَّاقُ ذُو اُ۬لْقُوَّةِ اِ۬لْمَتِينُۖ ﰹ', '', NULL),
(4714, '', '', NULL, NULL, 27, 7, 6, 4714, 59, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'فَإِنَّ لِلذِينَ ظَلَمُواْ ذَنُوباٗ مِّثْلَ ذَنُوبِ أَصْحَٰبِهِمْ فَلَا يَسْتَعْجِلُونِۖ ﰺ', '', NULL),
(4715, '', '', NULL, NULL, 27, 7, 7, 4715, 60, 523, NULL, 'الذَّاريَات ', 'Adh-Dhāriyāt', 51, 'فَوَيْلٞ لِّلذِينَ كَفَرُواْ مِنْ يَّوْمِهِمُ اُ۬لذِے يُوعَدُونَۖ ﰻ', '', NULL),
(4716, '', '', NULL, NULL, 27, 10, 10, 4716, 1, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'وَالطُّورِ وَكِتَٰبٖ مَّسْطُورٖ ﰀ', '', NULL),
(4717, '', '', NULL, NULL, 27, 10, 10, 4717, 2, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'فِے رَقّٖ مَّنشُورٖ ﰁ', '', NULL),
(4718, '', '', NULL, NULL, 27, 11, 10, 4718, 3, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'وَالْبَيْتِ اِ۬لْمَعْمُورِ ﰂ', '', NULL),
(4719, '', '', NULL, NULL, 27, 11, 11, 4719, 4, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'وَالسَّقْفِ اِ۬لْمَرْفُوعِ ﰃ', '', NULL),
(4720, '', '', NULL, NULL, 27, 11, 11, 4720, 5, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'وَالْبَحْرِ اِ۬لْمَسْجُورِ ﰄ', '', NULL),
(4721, '', '', NULL, NULL, 27, 12, 11, 4721, 6, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'إِنَّ عَذَابَ رَبِّكَ لَوَٰقِعٞۖ ﰅ', '', NULL),
(4722, '', '', NULL, NULL, 27, 12, 12, 4722, 7, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'مَّا لَهُۥ مِن دَافِعٖۖ ﰆ', '', NULL),
(4723, '', '', NULL, NULL, 27, 13, 12, 4723, 8, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'يَوْمَ تَمُورُ اُ۬لسَّمَآءُ مَوْراٗ ﰇ', '', NULL),
(4724, '', '', NULL, NULL, 27, 13, 13, 4724, 9, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'وَتَسِيرُ اُ۬لْجِبَالُ سَيْراٗۖ ﰈ', '', NULL),
(4725, '', '', NULL, NULL, 27, 13, 13, 4725, 10, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'فَوَيْلٞ يَوْمَئِذٖ لِّلْمُكَذِّبِينَ ﰉ', '', NULL),
(4726, '', '', NULL, NULL, 27, 14, 14, 4726, 11, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'اَ۬لذِينَ هُمْ فِے خَوْضٖ يَلْعَبُونَۖ ﰊ', '', NULL),
(4727, '', '', NULL, NULL, 27, 15, 14, 4727, 12, 523, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'يَوْمَ يُدَعُّونَ إِلَىٰ ن۪ارِ جَهَنَّمَ دَعّاًۖ هَٰذِهِ اِ۬لنَّارُ اُ۬لتِے كُنتُم بِهَا تُكَذِّبُونَۖ ﰋ', '', NULL),
(4728, '', '', NULL, NULL, 27, 1, 1, 4728, 13, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَفَسِحْرٌ هَٰذَآ أَمَ اَنتُمْ لَا تُبْصِرُونَۖ ﰌ', '', NULL),
(4729, '', '', NULL, NULL, 27, 2, 1, 4729, 14, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'اَ۪صْلَوْهَا فَاصْبِرُوٓاْ أَوْ لَا تَصْبِرُواْ سَوَآءٌ عَلَيْكُمُۥٓۖ إِنَّمَا تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَۖ ﰍ', '', NULL),
(4730, '', '', NULL, NULL, 27, 3, 3, 4730, 15, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'إِنَّ اَ۬لْمُتَّقِينَ فِے جَنَّٰتٖ وَنَعِيمٖ ﰎ', '', NULL),
(4731, '', '', NULL, NULL, 27, 4, 3, 4731, 16, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'فَٰكِهِينَ بِمَآ ءَات۪يٰهُمْ رَبُّهُمْۖ وَوَق۪يٰهُمْ رَبُّهُمْ عَذَابَ اَ۬لْجَحِيمِۖ ﰏ', '', NULL),
(4732, '', '', NULL, NULL, 27, 5, 4, 4732, 17, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'كُلُواْ وَاشْرَبُواْ هَنِيٓـٔاَۢ بِمَا كُنتُمْ تَعْمَلُونَ ﰐ', '', NULL),
(4733, '', '', NULL, NULL, 27, 6, 5, 4733, 18, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'مُتَّكِـِٕينَ عَلَىٰ سُرُرٖ مَّصْفُوفَةٖ وَزَوَّجْنَٰهُم بِحُورٍ عِينٖۖ ﰑ', '', NULL),
(4734, '', '', NULL, NULL, 27, 8, 6, 4734, 19, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'وَالذِينَ ءَامَنُواْ وَاتَّبَعَتْهُمْ ذُرِّيَّتُهُم بِإِيمَٰنٍ اَلْحَقْنَا بِهِمْ ذُرِّيَّٰتِهِمْ وَمَآ أَلَتْنَٰهُم مِّنْ عَمَلِهِم مِّن شَےْءٖۖ كُلُّ اُ۪مْرِےِٕۢ بِمَا كَسَبَ رَهِينٞۖ ﰒ', '', NULL),
(4735, '', '', NULL, NULL, 27, 8, 8, 4735, 20, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'وَأَمْدَدْنَٰهُم بِفَٰكِهَةٖ وَلَحْمٖ مِّمَّا يَشْتَهُونَ ﰓ', '', NULL),
(4736, '', '', NULL, NULL, 27, 9, 9, 4736, 21, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'يَتَنَٰزَعُونَ فِيهَا كَأْساٗ لَّا لَغْوٞ فِيهَا وَلَا تَاثِيمٞۖ ﰔ', '', NULL),
(4737, '', '', NULL, NULL, 27, 10, 9, 4737, 22, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, '۞ وَيَطُوفُ عَلَيْهِمْ غِلْمَانٞ لَّهُمْ كَأَنَّهُمْ لُؤْلُؤٞ مَّكْنُونٞۖ ﰕ', '', NULL),
(4738, '', '', NULL, NULL, 27, 11, 10, 4738, 23, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٖ يَتَسَآءَلُونَۖ ﰖ', '', NULL),
(4739, '', '', NULL, NULL, 27, 11, 11, 4739, 24, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'قَالُوٓاْ إِنَّا كُنَّا قَبْلُ فِےٓ أَهْلِنَا مُشْفِقِينَ ﰗ', '', NULL),
(4740, '', '', NULL, NULL, 27, 12, 12, 4740, 25, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'فَمَنَّ اَ۬للَّهُ عَلَيْنَا وَوَق۪يٰنَا عَذَابَ اَ۬لسَّمُومِۖ ﰘ', '', NULL),
(4741, '', '', NULL, NULL, 27, 13, 12, 4741, 26, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'إِنَّا كُنَّا مِن قَبْلُ نَدْعُوهُ أَنَّهُۥ هُوَ اَ۬لْبَرُّ اُ۬لرَّحِيمُۖ ﰙ', '', NULL),
(4742, '', '', NULL, NULL, 27, 14, 13, 4742, 27, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'فَذَكِّرْ فَمَآ أَنتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٖ وَلَا مَجْنُونٍۖ ﰚ', '', NULL),
(4743, '', '', NULL, NULL, 27, 15, 14, 4743, 28, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'اَمْ يَقُولُونَ شَاعِرٞ نَّتَرَبَّصُ بِهِۦ رَيْبَ اَ۬لْمَنُونِۖ ﰛ', '', NULL),
(4744, '', '', NULL, NULL, 27, 15, 15, 4744, 29, 524, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'قُلْ تَرَبَّصُواْۖ فَإِنِّے مَعَكُم مِّنَ اَ۬لْمُتَرَبِّصِينَۖ ﰜ', '', NULL),
(4745, '', '', NULL, NULL, 27, 1, 1, 4745, 30, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ تَامُرُهُمُۥٓ أَحْلَٰمُهُم بِهَٰذَآ أَمْ هُمْ قَوْمٞ طَاغُونَۖ ﰝ', '', NULL),
(4746, '', '', NULL, NULL, 27, 2, 1, 4746, 31, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ يَقُولُونَ تَقَوَّلَهُۥۖ بَل لَّا يُومِنُونَۖ ﰞ', '', NULL),
(4747, '', '', NULL, NULL, 27, 3, 2, 4747, 32, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'فَلْيَاتُواْ بِحَدِيثٖ مِّثْلِهِۦٓ إِن كَانُواْ صَٰدِقِينَۖ ﰟ', '', NULL),
(4748, '', '', NULL, NULL, 27, 3, 3, 4748, 33, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ خُلِقُواْ مِنْ غَيْرِ شَےْءٍ اَمْ هُمُ اُ۬لْخَٰلِقُونَۖ ﰠ', '', NULL),
(4749, '', '', NULL, NULL, 27, 4, 4, 4749, 34, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ خَلَقُواْ اُ۬لسَّمَٰوَٰتِ وَالَارْضَۖ بَل لَّا يُوقِنُونَۖ ﰡ', '', NULL),
(4750, '', '', NULL, NULL, 27, 5, 4, 4750, 35, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ عِندَهُمْ خَزَآئِنُ رَبِّكَ أَمْ هُمُ اُ۬لْمُصَيْطِرُونَۖ ﰢ', '', NULL),
(4751, '', '', NULL, NULL, 27, 6, 5, 4751, 36, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ لَهُمْ سُلَّمٞ يَسْتَمِعُونَ فِيهِۖ فَلْيَاتِ مُسْتَمِعُهُم بِسُلْطَٰنٖ مُّبِينٍۖ ﰣ', '', NULL),
(4752, '', '', NULL, NULL, 27, 7, 6, 4752, 37, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'اَمْ لَهُ اُ۬لْبَنَٰتُ وَلَكُمُ اُ۬لْبَنُونَۖ ﰤ', '', NULL),
(4753, '', '', NULL, NULL, 27, 8, 7, 4753, 38, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ تَسْـَٔلُهُمُۥٓ أَجْراٗ فَهُم مِّن مَّغْرَمٖ مُّثْقَلُونَۖ ﰥ', '', NULL),
(4754, '', '', NULL, NULL, 27, 8, 8, 4754, 39, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ عِندَهُمُ اُ۬لْغَيْبُ فَهُمْ يَكْتُبُونَۖ ﰦ', '', NULL),
(4755, '', '', NULL, NULL, 27, 9, 8, 4755, 40, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ يُرِيدُونَ كَيْداٗۖ فَالذِينَ كَفَرُواْ هُمُ اُ۬لْمَكِيدُونَۖ ﰧ', '', NULL),
(4756, '', '', NULL, NULL, 27, 10, 9, 4756, 41, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'أَمْ لَهُمُۥٓ إِلَٰهٌ غَيْرُ اُ۬للَّهِۖ سُبْحَٰنَ اَ۬للَّهِ عَمَّا يُشْرِكُونَۖ ﰨ', '', NULL),
(4757, '', '', NULL, NULL, 27, 11, 10, 4757, 42, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, '۞ وَإِنْ يَّرَوْاْ كِسْفاٗ مِّنَ اَ۬لسَّمَآءِ سَاقِطاٗ يَقُولُواْ سَحَابٞ مَّرْكُومٞۖ ﰩ', '', NULL),
(4758, '', '', NULL, NULL, 27, 12, 11, 4758, 43, 525, NULL, 'الطُّور ', 'Aṭ-Ṭūr', 52, 'فَذَرْهُمْ حَتَّىٰ يُلَٰقُواْ يَوْمَهُمُ اُ۬لذِے فِيهِ يَصْعَقُونَ ﰪ', '', NULL),
