(5054, '', '', NULL, NULL, 27, 10, 10, 5054, 99, 537, NULL, 'الوَاقِعة ', 'Al-Wāqi‘ah', 56, 'فَسَبِّحْ بِاسْمِ رَبِّكَ اَ۬لْعَظِيمِۖ ﱢ', '', NULL),
(5055, '', '', NULL, NULL, 27, 13, 13, 5055, 1, 537, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'سَبَّحَ لِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَالَارْضِۖ وَهُوَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ﰀ', '', NULL),
(5056, '', '', NULL, NULL, 27, 14, 13, 5056, 2, 537, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'لَهُۥ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالَارْضِ يُحْيِۦ وَيُمِيتُۖ وَهُوَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ﰁ', '', NULL),
(5057, '', '', NULL, NULL, 27, 15, 15, 5057, 3, 537, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'هُوَ اَ۬لَاوَّلُ وَالَاخِرُ وَالظَّٰهِرُ وَالْبَاطِنُۖ وَهُوَ بِكُلِّ شَےْءٍ عَلِيمٌۖ ﰂ', '', NULL),
(5058, '', '', NULL, NULL, 27, 4, 1, 5058, 4, 538, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'هُوَ اَ۬لذِے خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالَارْضَ فِے سِتَّةِ أَيَّامٖ ثُمَّ اَ۪سْتَو۪ىٰ عَلَى اَ۬لْعَرْشِۖ يَعْلَمُ مَا يَلِجُ فِے اِ۬لَارْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنزِلُ مِنَ اَ۬لسَّمَآءِ وَمَا يَعْرُجُ فِيهَاۖ وَهُوَ مَعَكُمُۥٓ أَيْنَ مَا كُنتُمْۖ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٞۖ ﰃ', '', NULL),
(5059, '', '', NULL, NULL, 27, 4, 4, 5059, 5, 538, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'لَّهُۥ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالَارْضِۖ وَإِلَى اَ۬للَّهِ تُرْجَعُ اُ۬لُامُورُۖ ﰄ', '', NULL),
(5060, '', '', NULL, NULL, 27, 6, 5, 5060, 6, 538, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'يُولِجُ اُ۬ليْلَ فِے اِ۬لنَّه۪ارِ وَيُولِجُ اُ۬لنَّهَارَ فِے اِ۬ليْلِۖ وَهُوَ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِۖ ﰅ', '', NULL),
(5061, '', '', NULL, NULL, 27, 7, 6, 5061, 7, 538, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, '۞ ءَامِنُواْ بِاللَّهِ وَرَسُولِهِۦ وَأَنفِقُواْ مِمَّا جَعَلَكُم مُّسْتَخْلَفِينَ فِيهِۖ فَالذِينَ ءَامَنُواْ مِنكُمْ وَأَنفَقُواْ لَهُمُۥٓ أَجْرٞ كَبِيرٞۖ ﰆ', '', NULL),
(5062, '', '', NULL, NULL, 27, 9, 8, 5062, 8, 538, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'وَمَا لَكُمْ لَا تُومِنُونَ بِاللَّهِ وَالرَّسُولُ يَدْعُوكُمْ لِتُومِنُواْ بِرَبِّكُمْ وَقَدَ اَخَذَ مِيثَٰقَكُمُۥٓ إِن كُنتُم مُّومِنِينَۖ ﰇ', '', NULL),
(5063, '', '', NULL, NULL, 27, 11, 9, 5063, 9, 538, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'هُوَ اَ۬لذِے يُنَزِّلُ عَلَىٰ عَبْدِهِۦٓ ءَايَٰتِۢ بَيِّنَٰتٖ لِّيُخْرِجَكُم مِّنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِۖ وَإِنَّ اَ۬للَّهَ بِكُمْ لَرَءُوفٞ رَّحِيمٞۖ ﰈ', '', NULL),
(5064, '', '', NULL, NULL, 27, 14, 11, 5064, 10, 538, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'وَمَا لَكُمُۥٓ أَلَّا تُنفِقُواْ فِے سَبِيلِ اِ۬للَّهِ وَلِلهِ مِيرَٰثُ اُ۬لسَّمَٰوَٰتِ وَالَارْضِۖ لَا يَسْتَوِے مِنكُم مَّنَ اَنفَقَ مِن قَبْلِ اِ۬لْفَتْحِ وَقَٰتَلَۖ أُوْلَٰٓئِكَ أَعْظَمُ دَرَجَةٗ مِّنَ اَ۬لذِينَ أَنفَقُواْ مِنۢ بَعْدُ وَقَٰتَلُواْۖ وَكُلّاٗ وَعَدَ اَ۬للَّهُ اُ۬لْحُسْن۪ىٰۖ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٞۖ ﰉ', '', NULL),
(5065, '', '', NULL, NULL, 27, 15, 14, 5065, 11, 538, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'مَّن ذَا اَ۬لذِے يُقْرِضُ اُ۬للَّهَ قَرْضاً حَسَناٗ فَيُضَٰعِفُهُۥ لَهُۥ وَلَهُۥٓ أَجْرٞ كَرِيمٞۖ ﰊ', '', NULL),
(5066, '', '', NULL, NULL, 27, 3, 1, 5066, 12, 539, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'يَوْمَ تَرَى اَ۬لْمُومِنِينَ وَالْمُومِنَٰتِ يَسْع۪ىٰ نُورُهُم بَيْنَ أَيْدِيهِمْ وَبِأَيْمَٰنِهِمۖ بُشْر۪يٰكُمُ اُ۬لْيَوْمَ جَنَّٰتٞ تَجْرِے مِن تَحْتِهَا اَ۬لَانْهَٰرُ خَٰلِدِينَ فِيهَاۖ ذَٰلِكَ هُوَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ﰋ', '', NULL),
(5067, '', '', NULL, NULL, 27, 8, 3, 5067, 13, 539, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'يَوْمَ يَقُولُ اُ۬لْمُنَٰفِقُونَ وَالْمُنَٰفِقَٰتُ لِلذِينَ ءَامَنُواْ اُ۟نظُرُونَا نَقْتَبِسْ مِن نُّورِكُمْ قِيلَ اَ۪رْجِعُواْ وَرَآءَكُمْ فَالْتَمِسُواْ نُوراٗۖ فَضُرِبَ بَيْنَهُم بِسُورٖ لَّهُۥ بَابُۢ بَاطِنُهُۥ فِيهِ اِ۬لرَّحْمَةُ وَظَٰهِرُهُۥ مِن قِبَلِهِ اِ۬لْعَذَابُۖ يُنَادُونَهُمُۥٓ أَلَمْ نَكُن مَّعَكُمْۖ قَالُواْ بَل۪ىٰ وَلَٰكِنَّكُمْ فَتَنتُمُۥٓ أَنفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ اُ۬لَامَانِيُّ حَتَّىٰ جَآءَ امْرُ اُ۬للَّهِ وَغَرَّكُم بِاللَّهِ اِ۬لْغَرُورُۖ ﰌ', '', NULL),
(5068, '', '', NULL, NULL, 27, 10, 8, 5068, 14, 539, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'فَالْيَوْمَ لَا يُوخَذُ مِنكُمْ فِدْيَةٞ وَلَا مِنَ اَ۬لذِينَ كَفَرُواْ مَأْو۪يٰكُمُ اُ۬لنَّارُ هِيَ مَوْل۪يٰكُمْۖ وَبِيسَ اَ۬لْمَصِيرُۖ ﰍ', '', NULL),
(5069, '', '', NULL, NULL, 27, 13, 10, 5069, 15, 539, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, '۞ أَلَمْ يَانِ لِلذِينَ ءَامَنُوٓاْ أَن تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اِ۬للَّهِ وَمَا نَزَلَ مِنَ اَ۬لْحَقِّۖ وَلَا يَكُونُواْ كَالذِينَ أُوتُواْ اُ۬لْكِتَٰبَ مِن قَبْلُ فَطَالَ عَلَيْهِمُ اُ۬لَامَدُ فَقَسَتْ قُلُوبُهُمْۖ وَكَثِيرٞ مِّنْهُمْ فَٰسِقُونَۖ ﰎ', '', NULL),
(5070, '', '', NULL, NULL, 27, 14, 13, 5070, 16, 539, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'اَ۪عْلَمُوٓاْ أَنَّ اَ۬للَّهَ يُحْيِ اِ۬لَارْضَ بَعْدَ مَوْتِهَاۖ قَدْ بَيَّنَّا لَكُمُ اُ۬لَايَٰتِ لَعَلَّكُمْ تَعْقِلُونَۖ ﰏ', '', NULL),
(5071, '', '', NULL, NULL, 27, 15, 14, 5071, 17, 539, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'إِنَّ اَ۬لْمُصَّدِّقِينَ وَالْمُصَّدِّقَٰتِ وَأَقْرَضُواْ اُ۬للَّهَ قَرْضاً حَسَناٗ يُضَٰعَفُ لَهُمْ وَلَهُمُۥٓ أَجْرٞ كَرِيمٞۖ ﰐ', '', NULL),
(5072, '', '', NULL, NULL, 27, 3, 1, 5072, 18, 540, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'وَالذِينَ ءَامَنُواْ بِاللَّهِ وَرُسُلِهِۦٓ أُوْلَٰٓئِكَ هُمُ اُ۬لصِّدِّيقُونَۖ وَالشُّهَدَآءُ عِندَ رَبِّهِمْۖ لَهُمُۥٓ أَجْرُهُمْ وَنُورُهُمْۖ وَالذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لْجَحِيمِۖ ﰑ', '', NULL),
(5073, '', '', NULL, NULL, 27, 7, 3, 5073, 19, 540, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'اِ۪عْلَمُوٓاْ أَنَّمَا اَ۬لْحَيَوٰةُ اُ۬لدُّنْي۪ا لَعِبٞ وَلَهْوٞ وَزِينَةٞ وَتَفَاخُرُۢ بَيْنَكُمْ وَتَكَاثُرٞ فِے اِ۬لَامْوَٰلِ وَالَاوْلَٰدِ كَمَثَلِ غَيْثٍ اَعْجَبَ اَ۬لْكُفَّارَ نَبَاتُهُۥ ثُمَّ يَهِيجُ فَتَر۪يٰهُ مُصْفَرّاٗ ثُمَّ يَكُونُ حُطَٰماٗۖ وَفِے اِ۬لَاخِرَةِ عَذَابٞ شَدِيدٞ وَمَغْفِرَةٞ مِّنَ اَ۬للَّهِ وَرِضْوَٰنٞۖ وَمَا اَ۬لْحَيَوٰةُ اُ۬لدُّنْي۪آ إِلَّا مَتَٰعُ اُ۬لْغُرُورِۖ ﰒ', '', NULL),
(5074, '', '', NULL, NULL, 27, 10, 7, 5074, 20, 540, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'سَابِقُوٓاْ إِلَىٰ مَغْفِرَةٖ مِّن رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ اِ۬لسَّمَآءِ وَالَارْضِ أُعِدَّتْ لِلذِينَ ءَامَنُواْ بِاللَّهِ وَرُسُلِهِۦۖ ذَٰلِكَ فَضْلُ اُ۬للَّهِ يُوتِيهِ مَنْ يَّشَآءُۖ وَاللَّهُ ذُو اُ۬لْفَضْلِ اِ۬لْعَظِيمِۖ ﰓ', '', NULL),
(5075, '', '', NULL, NULL, 27, 12, 10, 5075, 21, 540, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, '۞ مَآ أَصَابَ مِن مُّصِيبَةٖ فِے اِ۬لَارْضِ وَلَا فِےٓ أَنفُسِكُمُۥٓ إِلَّا فِے كِتَٰبٖ مِّن قَبْلِ أَن نَّبْرَأَهَآۖ إِنَّ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيرٞ ﰔ', '', NULL),
(5076, '', '', NULL, NULL, 27, 14, 12, 5076, 22, 540, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'لِّكَيْلَا تَاسَوْاْ عَلَىٰ مَا فَاتَكُمْ وَلَا تَفْرَحُواْ بِمَآ ءَات۪يٰكُمْۖ وَاللَّهُ لَا يُحِبُّ كُلَّ مُخْتَالٖ فَخُورٍۖ ﰕ', '', NULL),
(5077, '', '', NULL, NULL, 27, 15, 14, 5077, 23, 540, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'اِ۬لذِينَ يَبْخَلُونَ وَيَامُرُونَ اَ۬لنَّاسَ بِالْبُخْلِۖ وَمَنْ يَّتَوَلَّ فَإِنَّ اَ۬للَّهَ اَ۬لْغَنِيُّ اُ۬لْحَمِيدُۖ ﰖ', '', NULL),
(5078, '', '', NULL, NULL, 27, 4, 1, 5078, 24, 541, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'لَقَدَ اَرْسَلْنَا رُسُلَنَا بِالْبَيِّنَٰتِ وَأَنزَلْنَا مَعَهُمُ اُ۬لْكِتَٰبَ وَالْمِيزَانَ لِيَقُومَ اَ۬لنَّاسُ بِالْقِسْطِۖ وَأَنزَلْنَا اَ۬لْحَدِيدَ فِيهِ بَأْسٞ شَدِيدٞ وَمَنَٰفِعُ لِلنَّاسِۖ وَلِيَعْلَمَ اَ۬للَّهُ مَنْ يَّنصُرُهُۥ وَرُسُلَهُۥ بِالْغَيْبِۖ إِنَّ اَ۬للَّهَ قَوِيٌّ عَزِيزٞۖ ﰗ', '', NULL),
(5079, '', '', NULL, NULL, 27, 6, 4, 5079, 25, 541, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'وَلَقَدَ اَرْسَلْنَا نُوحاٗ وَإِبْرَٰهِيمَ وَجَعَلْنَا فِے ذُرِّيَّتِهِمَا اَ۬لنُّبُوٓءَةَ وَالْكِتَٰبَۖ فَمِنْهُم مُّهْتَدٖۖ وَكَثِيرٞ مِّنْهُمْ فَٰسِقُونَۖ ﰘ', '', NULL),
(5080, '', '', NULL, NULL, 27, 11, 6, 5080, 26, 541, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'ثُمَّ قَفَّيْنَا عَلَىٰٓ ءَاثٰ۪رِهِم بِرُسُلِنَا وَقَفَّيْنَا بِعِيسَى اَ۪بْنِ مَرْيَمَ وَءَاتَيْنَٰهُ اُ۬لِانجِيلَۖ وَجَعَلْنَا فِے قُلُوبِ اِ۬لذِينَ اَ۪تَّبَعُوهُ رَأْفَةٗ وَرَحْمَةٗ وَرَهْبَانِيَّةً اِ۪بْتَدَعُوهَا مَا كَتَبْنَٰهَا عَلَيْهِمُۥٓ إِلَّا اَ۪بْتِغَآءَ رِضْوَٰنِ اِ۬للَّهِۖ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَاۖ فَـَٔاتَيْنَا اَ۬لذِينَ ءَامَنُواْ مِنْهُمُۥٓ أَجْرَهُمْۖ وَكَثِيرٞ مِّنْهُمْ فَٰسِقُونَۖ ﰙ', '', NULL),
(5081, '', '', NULL, NULL, 27, 13, 11, 5081, 27, 541, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَءَامِنُواْ بِرَسُولِهِۦ يُوتِكُمْ كِفْلَيْنِ مِن رَّحْمَتِهِۦ وَيَجْعَل لَّكُمْ نُوراٗ تَمْشُونَ بِهِۦ وَيَغْفِرْ لَكُمْۖ وَاللَّهُ غَفُورٞ رَّحِيمٞ ﰚ', '', NULL),
(5082, '', '', NULL, NULL, 27, 15, 13, 5082, 28, 541, NULL, 'الحدِيد ', 'Al-Ḥadīd', 57, 'لِّيَ۬لَّا يَعْلَمَ أَهْلُ اُ۬لْكِتَٰبِ أَلَّا يَقْدِرُونَ عَلَىٰ شَےْءٖ مِّن فَضْلِ اِ۬للَّهِ وَأَنَّ اَ۬لْفَضْلَ بِيَدِ اِ۬للَّهِ يُوتِيهِ مَنْ يَّشَآءُۖ وَاللَّهُ ذُو اُ۬لْفَضْلِ اِ۬لْعَظِيمِۖ ﰛ', '', NULL),
(5083, '', '', NULL, NULL, 28, 4, 3, 5083, 1, 542, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'قَدْ سَمِعَ اَ۬للَّهُ قَوْلَ اَ۬لتِے تُجَٰدِلُكَ فِے زَوْجِهَا وَتَشْتَكِےٓ إِلَى اَ۬للَّهِ وَاللَّهُ يَسْمَعُ تَحَاوُرَكُمَآۖ إِنَّ اَ۬للَّهَ سَمِيعُۢ بَصِيرٌۖ ﰀ', '', NULL),
(5084, '', '', NULL, NULL, 28, 7, 4, 5084, 2, 542, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'اِ۬لذِينَ يَظَّهَّرُونَ مِنكُم مِّن نِّسَآئِهِم مَّا هُنَّ أُمَّهَٰتِهِمُۥٓۖ إِنُ ا۟مَّهَٰتُهُمُۥٓ إِلَّا اَ۬ل۪ےْ وَلَدْنَهُمْۖ وَإِنَّهُمْ لَيَقُولُونَ مُنكَراٗ مِّنَ اَ۬لْقَوْلِ وَزُوراٗۖ وَإِنَّ اَ۬للَّهَ لَعَفُوٌّ غَفُورٞۖ ﰁ', '', NULL),
(5085, '', '', NULL, NULL, 28, 9, 7, 5085, 3, 542, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'وَالذِينَ يَظَّهَّرُونَ مِن نِّسَآئِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُواْ فَتَحْرِيرُ رَقَبَةٖ مِّن قَبْلِ أَنْ يَّتَمَآسَّاۖ ذَٰلِكُمْ تُوعَظُونَ بِهِۦۖ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٞۖ ﰂ', '', NULL),
(5086, '', '', NULL, NULL, 28, 12, 9, 5086, 4, 542, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'فَمَن لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِن قَبْلِ أَنْ يَّتَمَآسَّاۖ فَمَن لَّمْ يَسْتَطِعْ فَإِطْعَامُ سِتِّينَ مِسْكِيناٗۖ ذَٰلِكَ لِتُومِنُواْ بِاللَّهِ وَرَسُولِهِۦۖ وَتِلْكَ حُدُودُ اُ۬للَّهِۖ وَلِلْكٰ۪فِرِينَ عَذَابٌ اَلِيمٌۖ ﰃ', '', NULL),
(5087, '', '', NULL, NULL, 28, 14, 12, 5087, 5, 542, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'اِنَّ اَ۬لذِينَ يُحَآدُّونَ اَ۬للَّهَ وَرَسُولَهُۥ كُبِتُواْ كَمَا كُبِتَ اَ۬لذِينَ مِن قَبْلِهِمْۖ وَقَدَ اَنزَلْنَآ ءَايَٰتِۢ بَيِّنَٰتٖۖ وَلِلْكٰ۪فِرِينَ عَذَابٞ مُّهِينٞۖ ﰄ', '', NULL),
(5088, '', '', NULL, NULL, 28, 15, 14, 5088, 6, 542, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'يَوْمَ يَبْعَثُهُمُ اُ۬للَّهُ جَمِيعاٗ فَيُنَبِّئُهُم بِمَا عَمِلُوٓاْۖ أَحْص۪يٰهُ اُ۬للَّهُ وَنَسُوهُۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ شَهِيدٌۖ ﰅ', '', NULL),
(5089, '', '', NULL, NULL, 28, 4, 1, 5089, 7, 543, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'اَلَمْ تَرَ أَنَّ اَ۬للَّهَ يَعْلَمُ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لَارْضِۖ مَا يَكُونُ مِن نَّجْو۪ىٰ ثَلَٰثَةٍ اِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ اِلَّا هُوَ سَادِسُهُمْ وَلَآ أَدْن۪ىٰ مِن ذَٰلِكَ وَلَآ أَكْثَرَ إِلَّا هُوَ مَعَهُمُۥٓ أَيْنَ مَا كَانُواْۖ ثُمَّ يُنَبِّئُهُم بِمَا عَمِلُواْ يَوْمَ اَ۬لْقِيَٰمَةِۖ إِنَّ اَ۬للَّهَ بِكُلِّ شَےْءٍ عَلِيمٌۖ ﰆ', '', NULL),
(5090, '', '', NULL, NULL, 28, 8, 4, 5090, 8, 543, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, '۞ اَلَمْ تَرَ إِلَى اَ۬لذِينَ نُهُواْ عَنِ اِ۬لنَّجْو۪ىٰ ثُمَّ يَعُودُونَ لِمَا نُهُواْ عَنْهُ وَيَتَنَٰجَوْنَ بِالِاثْمِ وَالْعُدْوَٰنِ وَمَعْصِيَتِ اِ۬لرَّسُولِۖ وَإِذَا جَآءُوكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اِ۬للَّهُ وَيَقُولُونَ فِےٓ أَنفُسِهِمْ لَوْلَا يُعَذِّبُنَا اَ۬للَّهُ بِمَا نَقُولُۖ حَسْبُهُمْ جَهَنَّمُۖ يَصْلَوْنَهَاۖ فَبِيسَ اَ۬لْمَصِيرُۖ ﰇ', '', NULL),
(5091, '', '', NULL, NULL, 28, 10, 8, 5091, 9, 543, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُوٓاْ إِذَا تَنَٰجَيْتُمْ فَلَا تَتَنَٰجَوْاْ بِالِاثْمِ وَالْعُدْوَٰنِ وَمَعْصِيَتِ اِ۬لرَّسُولِۖ وَتَنَٰجَوْاْ بِالْبِرِّ وَالتَّقْو۪ىٰۖ وَاتَّقُواْ اُ۬للَّهَ اَ۬لذِےٓ إِلَيْهِ تُحْشَرُونَۖ ﰈ', '', NULL),
(5092, '', '', NULL, NULL, 28, 12, 10, 5092, 10, 543, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'إِنَّمَا اَ۬لنَّجْو۪ىٰ مِنَ اَ۬لشَّيْطَٰنِ لِيُحْزِنَ اَ۬لذِينَ ءَامَنُواْ وَلَيْسَ بِضَآرِّهِمْ شَئْاً اِلَّا بِإِذْنِ اِ۬للَّهِۖ وَعَلَى اَ۬للَّهِ فَلْيَتَوَكَّلِ اِ۬لْمُومِنُونَۖ ﰉ', '', NULL),
(5093, '', '', NULL, NULL, 28, 15, 12, 5093, 11, 543, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُوٓاْ إِذَا قِيلَ لَكُمْ تَفَسَّحُواْ فِے اِ۬لْمَجْلِسِ فَافْسَحُواْ يَفْسَحِ اِ۬للَّهُ لَكُمْۖ وَإِذَا قِيلَ اَ۟نشُزُواْ فَانشُزُواْ يَرْفَعِ اِ۬للَّهُ اُ۬لذِينَ ءَامَنُواْ مِنكُمْ وَالذِينَ أُوتُواْ اُ۬لْعِلْمَ دَرَجَٰتٖۖ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٞۖ ﰊ', '', NULL),
(5094, '', '', NULL, NULL, 28, 3, 1, 5094, 12, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُوٓاْ إِذَا نَٰجَيْتُمُ اُ۬لرَّسُولَ فَقَدِّمُواْ بَيْنَ يَدَےْ نَجْو۪يٰكُمْ صَدَقَةٗۖ ذَٰلِكَ خَيْرٞ لَّكُمْ وَأَطْهَرُۖ فَإِن لَّمْ تَجِدُواْ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌۖ ﰋ', '', NULL),
(5095, '', '', NULL, NULL, 28, 5, 3, 5095, 13, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'آٰشْفَقْتُمُۥٓ أَن تُقَدِّمُواْ بَيْنَ يَدَےْ نَجْو۪يٰكُمْ صَدَقَٰتٖۖ فَإِذْ لَمْ تَفْعَلُواْ وَتَابَ اَ۬للَّهُ عَلَيْكُمْ فَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَۖ وَأَطِيعُواْ اُ۬للَّهَ وَرَسُولَهُۥۖ وَاللَّهُ خَبِيرُۢ بِمَا تَعْمَلُونَۖ ﰌ', '', NULL),
(5096, '', '', NULL, NULL, 28, 7, 5, 5096, 14, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, '۞ أَلَمْ تَرَ إِلَى اَ۬لذِينَ تَوَلَّوْاْ قَوْماً غَضِبَ اَ۬للَّهُ عَلَيْهِم مَّا هُم مِّنكُمْ وَلَا مِنْهُمْ وَيَحْلِفُونَ عَلَى اَ۬لْكَذِبِ وَهُمْ يَعْلَمُونَۖ ﰍ', '', NULL),
(5097, '', '', NULL, NULL, 28, 8, 7, 5097, 15, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'أَعَدَّ اَ۬للَّهُ لَهُمْ عَذَاباٗ شَدِيداًۖ اِنَّهُمْ سَآءَ مَا كَانُواْ يَعْمَلُونَۖ ﰎ', '', NULL),
(5098, '', '', NULL, NULL, 28, 9, 8, 5098, 16, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'اَ۪تَّخَذُوٓاْ أَيْمَٰنَهُمْ جُنَّةٗ فَصَدُّواْ عَن سَبِيلِ اِ۬للَّهِ فَلَهُمْ عَذَابٞ مُّهِينٞۖ ﰏ', '', NULL),
(5099, '', '', NULL, NULL, 28, 10, 9, 5099, 17, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'لَّن تُغْنِيَ عَنْهُمُۥٓ أَمْوَٰلُهُمْ وَلَآ أَوْلَٰدُهُم مِّنَ اَ۬للَّهِ شَئْاًۖ ا۟وْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنّ۪ارِ هُمْ فِيهَا خَٰلِدُونَۖ ﰐ', '', NULL),
(5100, '', '', NULL, NULL, 28, 12, 10, 5100, 18, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'يَوْمَ يَبْعَثُهُمُ اُ۬للَّهُ جَمِيعاٗ فَيَحْلِفُونَ لَهُۥ كَمَا يَحْلِفُونَ لَكُمْ وَيَحْسِبُونَ أَنَّهُمْ عَلَىٰ شَےْءٍۖ اَلَآ إِنَّهُمْ هُمُ اُ۬لْكَٰذِبُونَۖ ﰑ', '', NULL),
(5101, '', '', NULL, NULL, 28, 14, 12, 5101, 19, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'اَ۪سْتَحْوَذَ عَلَيْهِمُ اُ۬لشَّيْطَٰنُ فَأَنس۪يٰهُمْ ذِكْرَ اَ۬للَّهِۖ أُوْلَٰٓئِكَ حِزْبُ اُ۬لشَّيْطَٰنِۖ أَلَآ إِنَّ حِزْبَ اَ۬لشَّيْطَٰنِ هُمُ اُ۬لْخَٰسِرُونَۖ ﰒ', '', NULL),
(5102, '', '', NULL, NULL, 28, 15, 14, 5102, 20, 544, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'إِنَّ اَ۬لذِينَ يُحَآدُّونَ اَ۬للَّهَ وَرَسُولَهُۥٓ أُوْلَٰٓئِكَ فِے اِ۬لَاذَلِّينَۖ كَتَبَ اَ۬للَّهُ لَأَغْلِبَنَّ أَنَا وَرُسُلِيَۖ إِنَّ اَ۬للَّهَ قَوِيٌّ عَزِيزٞۖ ﰓ', '', NULL),
(5103, '', '', NULL, NULL, 28, 6, 1, 5103, 21, 545, NULL, 'المُجَادلة ', 'Al-Mujādilah', 58, 'لَّا تَجِدُ قَوْماٗ يُومِنُونَ بِاللَّهِ وَالْيَوْمِ اِ۬لَاخِرِ يُوَآدُّونَ مَنْ حَآدَّ اَ۬للَّهَ وَرَسُولَهُۥ وَلَوْ كَانُوٓاْ ءَابَآءَهُمُۥٓ أَوَ اَبْنَآءَهُمُۥٓ أَوِ اِخْوَٰنَهُمُۥٓ أَوْ عَشِيرَتَهُمُۥٓۖ أُوْلَٰٓئِكَ كَتَبَ فِے قُلُوبِهِمُ اُ۬لِايمَٰنَ وَأَيَّدَهُم بِرُوحٖ مِّنْهُ وَيُدْخِلُهُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لَانْهَٰرُ خَٰلِدِينَ فِيهَاۖ رَضِيَ اَ۬للَّهُ عَنْهُمْ وَرَضُواْ عَنْهُۖ أُوْلَٰٓئِكَ حِزْبُ اُ۬للَّهِۖ أَلَآ إِنَّ حِزْبَ اَ۬للَّهِ هُمُ اُ۬لْمُفْلِحُونَۖ ﰔ', '', NULL),
(5104, '', '', NULL, NULL, 28, 9, 9, 5104, 1, 545, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'سَبَّحَ لِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لَارْضِۖ وَهُوَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ﰀ', '', NULL),
(5105, '', '', NULL, NULL, 28, 14, 10, 5105, 2, 545, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'هُوَ اَ۬لذِےٓ أَخْرَجَ اَ۬لذِينَ كَفَرُواْ مِنَ اَهْلِ اِ۬لْكِتَٰبِ مِن دِيٰ۪رِهِمْ لِأَوَّلِ اِ۬لْحَشْرِۖ مَا ظَنَنتُمُۥٓ أَنْ يَّخْرُجُواْۖ وَظَنُّوٓاْ أَنَّهُم مَّانِعَتُهُمْ حُصُونُهُم مِّنَ اَ۬للَّهِۖ فَأَت۪يٰهُمُ اُ۬للَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُواْۖ ۞ وَقَذَفَ فِے قُلُوبِهِمُ اُ۬لرُّعْبَۖ يُخْرِبُونَ بُيُوتَهُم بِأَيْدِيهِمْ وَأَيْدِے اِ۬لْمُومِنِينَۖ فَاعْتَبِرُواْ يَٰٓأُوْلِے اِ۬لَابْصٰ۪رِۖ ﰁ', '', NULL),
(5106, '', '', NULL, NULL, 28, 15, 14, 5106, 3, 545, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'وَلَوْلَآ أَن كَتَبَ اَ۬للَّهُ عَلَيْهِمُ اُ۬لْجَلَآءَ لَعَذَّبَهُمْ فِے اِ۬لدُّنْي۪اۖ وَلَهُمْ فِے اِ۬لَاخِرَةِ عَذَابُ اُ۬لنّ۪ارِۖ ﰂ', '', NULL),
(5107, '', '', NULL, NULL, 28, 2, 1, 5107, 4, 546, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'ذَٰلِكَ بِأَنَّهُمْ شَآقُّواْ اُ۬للَّهَ وَرَسُولَهُۥۖ وَمَنْ يُّشَآقِّ اِ۬للَّهَ فَإِنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعِقَابِۖ ﰃ', '', NULL),
(5108, '', '', NULL, NULL, 28, 3, 2, 5108, 5, 546, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'مَا قَطَعْتُم مِّن لِّينَةٍ اَوْ تَرَكْتُمُوهَا قَآئِمَةً عَلَىٰٓ أُصُولِهَا فَبِإِذْنِ اِ۬للَّهِ وَلِيُخْزِيَ اَ۬لْفَٰسِقِينَۖ ﰄ', '', NULL),
(5109, '', '', NULL, NULL, 28, 5, 3, 5109, 6, 546, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'وَمَآ أَفَآءَ اَ۬للَّهُ عَلَىٰ رَسُولِهِۦ مِنْهُمْ فَمَآ أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٖ وَلَا رِكَابٖۖ وَلَٰكِنَّ اَ۬للَّهَ يُسَلِّطُ رُسُلَهُۥ عَلَىٰ مَنْ يَّشَآءُۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ﰅ', '', NULL),
(5110, '', '', NULL, NULL, 28, 9, 6, 5110, 7, 546, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'مَّآ أَفَآءَ اَ۬للَّهُ عَلَىٰ رَسُولِهِۦ مِنَ اَهْلِ اِ۬لْقُر۪ىٰ فَلِلهِ وَلِلرَّسُولِ وَلِذِے اِ۬لْقُرْب۪ىٰ وَالْيَتَٰم۪ىٰ وَالْمَسَٰكِينِ وَابْنِ اِ۬لسَّبِيلِ كَےْ لَا يَكُونَ دُولَةَۢ بَيْنَ اَ۬لَاغْنِيَآءِ مِنكُمْۖ وَمَآ ءَات۪يٰكُمُ اُ۬لرَّسُولُ فَخُذُوهُۖ وَمَا نَه۪يٰكُمْ عَنْهُ فَانتَهُواْۖ وَاتَّقُواْ اُ۬للَّهَۖ إِنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعِقَابِۖ ﰆ', '', NULL),
(5111, '', '', NULL, NULL, 28, 12, 10, 5111, 8, 546, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'لِلْفُقَرَآءِ اِ۬لْمُهَٰجِرِينَ اَ۬لذِينَ أُخْرِجُواْ مِن دِيٰ۪رِهِمْ وَأَمْوَٰلِهِمْ يَبْتَغُونَ فَضْلاٗ مِّنَ اَ۬للَّهِ وَرِضْوَٰناٗ وَيَنصُرُونَ اَ۬للَّهَ وَرَسُولَهُۥٓۖ أُوْلَٰٓئِكَ هُمُ اُ۬لصَّٰدِقُونَۖ ﰇ', '', NULL),
(5112, '', '', NULL, NULL, 28, 15, 12, 5112, 9, 546, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'وَالذِينَ تَبَوَّءُو اُ۬لدَّارَ وَالِايمَٰنَ مِن قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ إِلَيْهِمْ وَلَا يَجِدُونَ فِے صُدُورِهِمْ حَاجَةٗ مِّمَّآ أُوتُواْ وَيُوثِرُونَ عَلَىٰٓ أَنفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٞۖ وَمَنْ يُّوقَ شُحَّ نَفْسِهِۦ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُفْلِحُونَۖ ﰈ', '', NULL),
(5113, '', '', NULL, NULL, 28, 3, 1, 5113, 10, 547, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'وَالذِينَ جَآءُو مِنۢ بَعْدِهِمْ يَقُولُونَ رَبَّنَا اَ۪غْفِرْ لَنَا وَلِإِخْوَٰنِنَا اَ۬لذِينَ سَبَقُونَا بِالِايمَٰنِ وَلَا تَجْعَلْ فِے قُلُوبِنَا غِلّاٗ لِّلذِينَ ءَامَنُواْۖ رَبَّنَآ إِنَّكَ رَءُوفٞ رَّحِيمٌۖ ﰉ', '', NULL),
(5114, '', '', NULL, NULL, 28, 6, 3, 5114, 11, 547, NULL, 'الحَشر ', 'Al-Ḥashr', 59, '۞ اَلَمْ تَرَ إِلَى اَ۬لذِينَ نَافَقُواْ يَقُولُونَ لِإِخْوَٰنِهِمُ اُ۬لذِينَ كَفَرُواْ مِنَ اَهْلِ اِ۬لْكِتَٰبِ لَئِنُ ا۟خْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيعُ فِيكُمُۥٓ أَحَداً اَبَداٗ وَإِن قُوتِلْتُمْ لَنَنصُرَنَّكُمْۖ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَٰذِبُونَۖ ﰊ', '', NULL),
(5115, '', '', NULL, NULL, 28, 8, 7, 5115, 12, 547, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'لَئِنُ ا۟خْرِجُواْ لَا يَخْرُجُونَ مَعَهُمْۖ وَلَئِن قُوتِلُواْ لَا يَنصُرُونَهُمْۖ وَلَئِن نَّصَرُوهُمْ لَيُوَلُّنَّ اَ۬لَادْبَٰرَ ثُمَّ لَا يُنصَرُونَۖ ﰋ', '', NULL),
(5116, '', '', NULL, NULL, 28, 10, 8, 5116, 13, 547, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'لَأَنتُمُۥٓ أَشَدُّ رَهْبَةٗ فِے صُدُورِهِم مِّنَ اَ۬للَّهِۖ ذَٰلِكَ بِأَنَّهُمْ قَوْمٞ لَّا يَفْقَهُونَۖ ﰌ', '', NULL),
(5117, '', '', NULL, NULL, 28, 12, 10, 5117, 14, 547, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'لَا يُقَٰتِلُونَكُمْ جَمِيعاً اِلَّا فِے قُرىٗ مُّحَصَّنَةٍ اَوْ مِنْ وَّرَآءِ جُدُرِۢۖ بَأْسُهُم بَيْنَهُمْ شَدِيدٞۖ تَحْسِبُهُمْ جَمِيعاٗ وَقُلُوبُهُمْ شَتّ۪ىٰۖ ذَٰلِكَ بِأَنَّهُمْ قَوْمٞ لَّا يَعْقِلُونَۖ ﰍ', '', NULL),
(5118, '', '', NULL, NULL, 28, 14, 12, 5118, 15, 547, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'كَمَثَلِ اِ۬لذِينَ مِن قَبْلِهِمْ قَرِيباٗۖ ذَاقُواْ وَبَالَ أَمْرِهِمْۖ وَلَهُمْ عَذَابٌ اَلِيمٞۖ ﰎ', '', NULL),
(5119, '', '', NULL, NULL, 28, 15, 14, 5119, 16, 547, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'كَمَثَلِ اِ۬لشَّيْطَٰنِ إِذْ قَالَ لِلِانسَٰنِ اِ۟كْفُرْ فَلَمَّا كَفَرَ قَالَ إِنِّے بَرِےٓءٞ مِّنكَ إِنِّيَ أَخَافُ اُ۬للَّهَ رَبَّ اَ۬لْعَٰلَمِينَۖ ﰏ', '', NULL),
(5120, '', '', NULL, NULL, 28, 2, 1, 5120, 17, 548, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'فَكَانَ عَٰقِبَتَهُمَآ أَنَّهُمَا فِے اِ۬لنّ۪ارِ خَالِدَيْنِ فِيهَاۖ وَذَٰلِكَ جَزَٰٓؤُاْ اُ۬لظَّٰلِمِينَۖ ﰐ', '', NULL),
(5121, '', '', NULL, NULL, 28, 4, 2, 5121, 18, 548, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَلْتَنظُرْ نَفْسٞ مَّا قَدَّمَتْ لِغَدٖۖ وَاتَّقُواْ اُ۬للَّهَۖ إِنَّ اَ۬للَّهَ خَبِيرُۢ بِمَا تَعْمَلُونَۖ ﰑ', '', NULL),
(5122, '', '', NULL, NULL, 28, 5, 4, 5122, 19, 548, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'وَلَا تَكُونُواْ كَالذِينَ نَسُواْ اُ۬للَّهَ فَأَنس۪يٰهُمُۥٓ أَنفُسَهُمُۥٓۖ أُوْلَٰٓئِكَ هُمُ اُ۬لْفَٰسِقُونَۖ ﰒ', '', NULL),
(5123, '', '', NULL, NULL, 28, 7, 6, 5123, 20, 548, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'لَا يَسْتَوِےٓ أَصْحَٰبُ اُ۬لنّ۪ارِ وَأَصْحَٰبُ اُ۬لْجَنَّةِۖ أَصْحَٰبُ اُ۬لْجَنَّةِ هُمُ اُ۬لْفَآئِزُونَۖ ﰓ', '', NULL),
(5124, '', '', NULL, NULL, 28, 9, 7, 5124, 21, 548, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'لَوَ اَنزَلْنَا هَٰذَا اَ۬لْقُرْءَانَ عَلَىٰ جَبَلٖ لَّرَأَيْتَهُۥ خَٰشِعاٗ مُّتَصَدِّعاٗ مِّنْ خَشْيَةِ اِ۬للَّهِۖ وَتِلْكَ اَ۬لَامْثَٰلُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَۖ ﰔ', '', NULL),
(5125, '', '', NULL, NULL, 28, 11, 10, 5125, 22, 548, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'هُوَ اَ۬للَّهُ اُ۬لذِے لَآ إِلَٰهَ إِلَّا هُوَۖ عَٰلِمُ اُ۬لْغَيْبِ وَالشَّهَٰدَةِۖ هُوَ اَ۬لرَّحْمَٰنُ اُ۬لرَّحِيمُۖ ﰕ', '', NULL),
(5126, '', '', NULL, NULL, 28, 13, 11, 5126, 23, 548, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'هُوَ اَ۬للَّهُ اُ۬لذِے لَآ إِلَٰهَ إِلَّا هُوَ اَ۬لْمَلِكُ اُ۬لْقُدُّوسُ اُ۬لسَّلَٰمُ اُ۬لْمُومِنُ اُ۬لْمُهَيْمِنُ اُ۬لْعَزِيزُ اُ۬لْجَبَّارُ اُ۬لْمُتَكَبِّرُۖ سُبْحَٰنَ اَ۬للَّهِ عَمَّا يُشْرِكُونَۖ ﰖ', '', NULL),
(5127, '', '', NULL, NULL, 28, 15, 13, 5127, 24, 548, NULL, 'الحَشر ', 'Al-Ḥashr', 59, 'هُوَ اَ۬للَّهُ اُ۬لْخَٰلِقُ اُ۬لْبَارِۓُ اُ۬لْمُصَوِّرُۖ لَهُ اُ۬لَاسْمَآءُ اُ۬لْحُسْن۪ىٰۖ يُسَبِّحُ لَهُۥ مَا فِے اِ۬لسَّمَٰوَٰتِ وَالَارْضِۖ وَهُوَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ﰗ', '', NULL),
(5128, '', '', NULL, NULL, 28, 7, 3, 5128, 1, 549, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ عَدُوِّے وَعَدُوَّكُمُۥٓ أَوْلِيَآءَۖ تُلْقُونَ إِلَيْهِم بِالْمَوَدَّةِ وَقَدْ كَفَرُواْ بِمَا جَآءَكُم مِّنَ اَ۬لْحَقِّۖ يُخْرِجُونَ اَ۬لرَّسُولَ وَإِيَّاكُمُۥٓ أَن تُومِنُواْ بِاللَّهِ رَبِّكُمُۥٓۖ إِن كُنتُمْ خَرَجْتُمْ جِهَٰداٗ فِے سَبِيلِے وَابْتِغَآءَ مَرْضَاتِے تُسِرُّونَ إِلَيْهِم بِالْمَوَدَّةِ وَأَنَآ أَعْلَمُ بِمَآ أَخْفَيْتُمْ وَمَآ أَعْلَنتُمْۖ وَمَنْ يَّفْعَلْهُ مِنكُمْ فَقَد ضَّلَّ سَوَآءَ اَ۬لسَّبِيلِۖ ﰀ', '', NULL),
(5129, '', '', NULL, NULL, 28, 8, 7, 5129, 2, 549, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'إِنْ يَّثْقَفُوكُمْ يَكُونُواْ لَكُمُۥٓ أَعْدَآءٗ وَيَبْسُطُوٓاْ إِلَيْكُمُۥٓ أَيْدِيَهُمْ وَأَلْسِنَتَهُم بِالسُّوٓءِۖ وَوَدُّواْ لَوْ تَكْفُرُونَۖ ﰁ', '', NULL),
(5130, '', '', NULL, NULL, 28, 10, 8, 5130, 3, 549, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'لَن تَنفَعَكُمُۥٓ أَرْحَامُكُمْ وَلَآ أَوْلَٰدُكُمْۖ يَوْمَ اَ۬لْقِيَٰمَةِ يُفْصَلُ بَيْنَكُمْۖ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٞۖ ﰂ', '', NULL),
(5131, '', '', NULL, NULL, 28, 14, 10, 5131, 4, 549, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'قَدْ كَانَتْ لَكُمُۥٓ إِسْوَةٌ حَسَنَةٞ فِےٓ إِبْرَٰهِيمَ وَالذِينَ مَعَهُۥٓ إِذْ قَالُواْ لِقَوْمِهِمُۥٓ إِنَّا بُرَءَٰٓؤُاْ مِنكُمْ وَمِمَّا تَعْبُدُونَ مِن دُونِ اِ۬للَّهِ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ اُ۬لْعَدَٰوَةُ وَالْبَغْضَآءُ اَ۬بَداً حَتَّىٰ تُومِنُواْ بِاللَّهِ وَحْدَهُۥٓ إِلَّا قَوْلَ إِبْرَٰهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَمَآ أَمْلِكُ لَكَ مِنَ اَ۬للَّهِ مِن شَےْءٖۖ رَّبَّنَا عَلَيْكَ تَوَكَّلْنَا وَإِلَيْكَ أَنَبْنَا وَإِلَيْكَ اَ۬لْمَصِيرُۖ ﰃ', '', NULL),
(5132, '', '', NULL, NULL, 28, 15, 14, 5132, 5, 549, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةٗ لِّلذِينَ كَفَرُواْۖ وَاغْفِرْ لَنَا رَبَّنَآۖ إِنَّكَ أَنتَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ﰄ', '', NULL),
(5133, '', '', NULL, NULL, 28, 2, 1, 5133, 6, 550, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'لَقَدْ كَانَ لَكُمْ فِيهِمُۥٓ إِسْوَةٌ حَسَنَةٞ لِّمَن كَانَ يَرْجُواْ اُ۬للَّهَ وَالْيَوْمَ اَ۬لَاخِرَۖ وَمَنْ يَّتَوَلَّ فَإِنَّ اَ۬للَّهَ هُوَ اَ۬لْغَنِيُّ اُ۬لْحَمِيدُۖ ﰅ', '', NULL),
(5134, '', '', NULL, NULL, 28, 3, 2, 5134, 7, 550, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, '۞ عَسَى اَ۬للَّهُ أَنْ يَّجْعَلَ بَيْنَكُمْ وَبَيْنَ اَ۬لذِينَ عَادَيْتُم مِّنْهُم مَّوَدَّةٗۖ وَاللَّهُ قَدِيرٞۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ﰆ', '', NULL),
(5135, '', '', NULL, NULL, 28, 5, 4, 5135, 8, 550, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'لَّا يَنْه۪يٰكُمُ اُ۬للَّهُ عَنِ اِ۬لذِينَ لَمْ يُقَٰتِلُوكُمْ فِے اِ۬لدِّينِ وَلَمْ يُخْرِجُوكُم مِّن دِيٰ۪رِكُمُۥٓ أَن تَبَرُّوهُمْ وَتُقْسِطُوٓاْ إِلَيْهِمُۥٓۖ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لْمُقْسِطِينَۖ ﰇ', '', NULL),
(5136, '', '', NULL, NULL, 28, 8, 6, 5136, 9, 550, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'إِنَّمَا يَنْه۪يٰكُمُ اُ۬للَّهُ عَنِ اِ۬لذِينَ قَٰتَلُوكُمْ فِے اِ۬لدِّينِ وَأَخْرَجُوكُم مِّن دِيٰ۪رِكُمْ وَظَٰهَرُواْ عَلَىٰٓ إِخْرَاجِكُمُۥٓ أَن تَوَلَّوْهُمْۖ وَمَنْ يَّتَوَلَّهُمْ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَۖ ﰈ', '', NULL),
(5137, '', '', NULL, NULL, 28, 13, 8, 5137, 10, 550, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُوٓاْ إِذَا جَآءَكُمُ اُ۬لْمُومِنَٰتُ مُهَٰجِرَٰتٖ فَامْتَحِنُوهُنَّۖ اَ۬للَّهُ أَعْلَمُ بِإِيمَٰنِهِنَّۖ فَإِنْ عَلِمْتُمُوهُنَّ مُومِنَٰتٖ فَلَا تَرْجِعُوهُنَّ إِلَى اَ۬لْكُفّ۪ارِۖ لَا هُنَّ حِلّٞ لَّهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّۖ وَءَاتُوهُم مَّآ أَنفَقُواْۖ وَلَا جُنَاحَ عَلَيْكُمُۥٓ أَن تَنكِحُوهُنَّ إِذَآ ءَاتَيْتُمُوهُنَّ أُجُورَهُنَّۖ وَلَا تُمْسِكُواْ بِعِصَمِ اِ۬لْكَوَافِرِۖ وَسْـَٔلُواْ مَآ أَنفَقْتُمْ وَلْيَسْـَٔلُواْ مَآ أَنفَقُواْۖ ذَٰلِكُمْ حُكْمُ اُ۬للَّهِ يَحْكُمُ بَيْنَكُمْۖ وَاللَّهُ عَلِيمٌ حَكِيمٞۖ ﰉ', '', NULL),
(5138, '', '', NULL, NULL, 28, 15, 13, 5138, 11, 550, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'وَإِن فَاتَكُمْ شَےْءٞ مِّنَ اَزْوَٰجِكُمُۥٓ إِلَى اَ۬لْكُفّ۪ارِ فَعَاقَبْتُمْ فَـَٔاتُواْ اُ۬لذِينَ ذَهَبَتَ اَزْوَٰجُهُم مِّثْلَ مَآ أَنفَقُواْۖ وَاتَّقُواْ اُ۬للَّهَ اَ۬لذِےٓ أَنتُم بِهِۦ مُومِنُونَۖ ﰊ', '', NULL),
(5139, '', '', NULL, NULL, 28, 4, 1, 5139, 12, 551, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'يَٰٓأَيُّهَا‏ اَ۬لنَّبِےٓءُ اِ۪ذَا جَآءَكَ اَ۬لْمُومِنَٰتُ يُبَايِعْنَكَ عَلَىٰٓ أَن لَّا يُشْرِكْنَ بِاللَّهِ شَئْاٗ وَلَا يَسْرِقْنَ وَلَا يَزْنِينَ وَلَا يَقْتُلْنَ أَوْلَٰدَهُنَّ وَلَا يَاتِينَ بِبُهْتَٰنٖ يَفْتَرِينَهُۥ بَيْنَ أَيْدِيهِنَّ وَأَرْجُلِهِنَّ وَلَا يَعْصِينَكَ فِے مَعْرُوفٖ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اَ۬للَّهَۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ﰋ', '', NULL),
(5140, '', '', NULL, NULL, 28, 6, 5, 5140, 13, 551, NULL, 'المُمتَحنَة ', 'Al-Mumtaḥanah', 60, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَتَوَلَّوْاْ قَوْماً غَضِبَ اَ۬للَّهُ عَلَيْهِمْ قَدْ يَئِسُواْ مِنَ اَ۬لَاخِرَةِ كَمَا يَئِسَ اَ۬لْكُفَّارُ مِنَ اَصْحَٰبِ اِ۬لْقُبُورِۖ ﰌ', '', NULL),
(5141, '', '', NULL, NULL, 28, 9, 9, 5141, 1, 551, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'سَبَّحَ لِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لَارْضِۖ وَهُوَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ﰀ', '', NULL),
(5142, '', '', NULL, NULL, 28, 10, 10, 5142, 2, 551, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَۖ ﰁ', '', NULL),
(5143, '', '', NULL, NULL, 28, 11, 11, 5143, 3, 551, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'كَبُرَ مَقْتاً عِندَ اَ۬للَّهِ أَن تَقُولُواْ مَا لَا تَفْعَلُونَۖ ﰂ', '', NULL),
(5144, '', '', NULL, NULL, 28, 13, 11, 5144, 4, 551, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لذِينَ يُقَٰتِلُونَ فِے سَبِيلِهِۦ صَفّاٗ كَأَنَّهُم بُنْيَٰنٞ مَّرْصُوصٞۖ ﰃ', '', NULL),
(5145, '', '', NULL, NULL, 28, 15, 13, 5145, 5, 551, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'وَإِذْ قَالَ مُوس۪ىٰ لِقَوْمِهِۦ يَٰقَوْمِ لِمَ تُوذُونَنِے وَقَد تَّعْلَمُونَ أَنِّے رَسُولُ اُ۬للَّهِ إِلَيْكُمْۖ فَلَمَّا زَاغُوٓاْ أَزَاغَ اَ۬للَّهُ قُلُوبَهُمْۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لْفَٰسِقِينَۖ ﰄ', '', NULL),
(5146, '', '', NULL, NULL, 28, 3, 1, 5146, 6, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'وَإِذْ قَالَ عِيسَى اَ۪بْنُ مَرْيَمَ يَٰبَنِےٓ إِسْرَآءِيلَ إِنِّے رَسُولُ اُ۬للَّهِ إِلَيْكُم مُّصَدِّقاٗ لِّمَا بَيْنَ يَدَيَّ مِنَ اَ۬لتَّوْر۪يٰةِ وَمُبَشِّراَۢ بِرَسُولٖ يَاتِے مِنۢ بَعْدِيَ اَ۪سْمُهُۥٓ أَحْمَدُۖ فَلَمَّا جَآءَهُم بِالْبَيِّنَٰتِ قَالُواْ هَٰذَا سِحْرٞ مُّبِينٞۖ ﰅ', '', NULL),
(5147, '', '', NULL, NULL, 28, 4, 3, 5147, 7, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'وَمَنَ اَظْلَمُ مِمَّنِ اِ۪فْتَر۪ىٰ عَلَى اَ۬للَّهِ اِ۬لْكَذِبَ وَهُوَ يُدْع۪ىٰٓ إِلَى اَ۬لِاسْلَٰمِۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لظَّٰلِمِينَۖ ﰆ', '', NULL),
(5148, '', '', NULL, NULL, 28, 6, 5, 5148, 8, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'يُرِيدُونَ لِيُطْفِـُٔواْ نُورَ اَ۬للَّهِ بِأَفْوَٰهِهِمْ وَاللَّهُ مُتِمّٞ نُّورَهُۥ وَلَوْ كَرِهَ اَ۬لْكَٰفِرُونَۖ ﰇ', '', NULL),
(5149, '', '', NULL, NULL, 28, 7, 6, 5149, 9, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'هُوَ اَ۬لذِےٓ أَرْسَلَ رَسُولَهُۥ بِالْهُد۪ىٰ وَدِينِ اِ۬لْحَقِّ لِيُظْهِرَهُۥ عَلَى اَ۬لدِّينِ كُلِّهِۦ وَلَوْ كَرِهَ اَ۬لْمُشْرِكُونَۖ ﰈ', '', NULL),
(5150, '', '', NULL, NULL, 28, 8, 7, 5150, 10, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ هَلَ اَدُلُّكُمْ عَلَىٰ تِجَٰرَةٖ تُنجِيكُم مِّنْ عَذَابٍ اَلِيمٖ ﰉ', '', NULL),
(5151, '', '', NULL, NULL, 28, 9, 8, 5151, 11, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'تُومِنُونَ بِاللَّهِ وَرَسُولِهِۦ وَتُجَٰهِدُونَ فِے سَبِيلِ اِ۬للَّهِ بِأَمْوَٰلِكُمْ وَأَنفُسِكُمْ ذَٰلِكُمْ خَيْرٞ لَّكُمُۥٓ إِن كُنتُمْ تَعْلَمُونَ ﰊ', '', NULL),
(5152, '', '', NULL, NULL, 28, 11, 10, 5152, 12, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَيُدْخِلْكُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لَانْهَٰرُ وَمَسَٰكِنَ طَيِّبَةٗ فِے جَنَّٰتِ عَدْنٖۖ ذَٰلِكَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ﰋ', '', NULL),
(5153, '', '', NULL, NULL, 28, 12, 11, 5153, 13, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'وَأُخْر۪ىٰ تُحِبُّونَهَا نَصْرٞ مِّنَ اَ۬للَّهِ وَفَتْحٞ قَرِيبٞۖ وَبَشِّرِ اِ۬لْمُومِنِينَۖ ﰌ', '', NULL),
(5154, '', '', NULL, NULL, 28, 15, 12, 5154, 14, 552, NULL, 'الصَّف ', 'Aṣ-Ṣaff', 61, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ كُونُوٓاْ أَنصَاراٗ لِّلهِ كَمَا قَالَ عِيسَى اَ۪بْنُ مَرْيَمَ لِلْحَوَارِيِّـۧنَ مَنَ اَنصَارِيَ إِلَى اَ۬للَّهِۖ قَالَ اَ۬لْحَوَارِيُّونَ نَحْنُ أَنصَارُ اُ۬للَّهِۖ فَـَٔامَنَت طَّآئِفَةٞ مِّنۢ بَنِےٓ إِسْرَآءِيلَ وَكَفَرَت طَّآئِفَةٞۖ فَأَيَّدْنَا اَ۬لذِينَ ءَامَنُواْ عَلَىٰ عَدُوِّهِمْ فَأَصْبَحُواْ ظَٰهِرِينَۖ ﰍ', '', NULL),
(5155, '', '', NULL, NULL, 28, 4, 3, 5155, 1, 553, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'يُسَبِّحُ لِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لَارْضِ اِ۬لْمَلِكِ اِ۬لْقُدُّوسِ اِ۬لْعَزِيزِ اِ۬لْحَكِيمِۖ ﰀ', '', NULL),
(5156, '', '', NULL, NULL, 28, 6, 4, 5156, 2, 553, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'هُوَ اَ۬لذِے بَعَثَ فِے اِ۬لُامِّيِّـۧنَ رَسُولاٗ مِّنْهُمْ يَتْلُواْ عَلَيْهِمُۥٓ ءَايَٰتِهِۦ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ اُ۬لْكِتَٰبَ وَالْحِكْمَةَ وَإِن كَانُواْ مِن قَبْلُ لَفِے ضَلَٰلٖ مُّبِينٖ ﰁ', '', NULL),
(5157, '', '', NULL, NULL, 28, 7, 6, 5157, 3, 553, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'وَءَاخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُواْ بِهِمْۖ وَهُوَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ﰂ', '', NULL),
(5158, '', '', NULL, NULL, 28, 8, 7, 5158, 4, 553, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'ذَٰلِكَ فَضْلُ اُ۬للَّهِ يُوتِيهِ مَنْ يَّشَآءُۖ وَاللَّهُ ذُو اُ۬لْفَضْلِ اِ۬لْعَظِيمِۖ ﰃ', '', NULL),
(5159, '', '', NULL, NULL, 28, 10, 8, 5159, 5, 553, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'مَثَلُ اُ۬لذِينَ حُمِّلُواْ اُ۬لتَّوْر۪يٰةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ اِ۬لْحِم۪ارِ يَحْمِلُ أَسْفَاراَۢۖ بِيسَ مَثَلُ اُ۬لْقَوْمِ اِ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِ اِ۬للَّهِۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لظَّٰلِمِينَۖ ﰄ', '', NULL),
(5160, '', '', NULL, NULL, 28, 12, 11, 5160, 6, 553, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'قُلْ يَٰٓأَيُّهَا اَ۬لذِينَ هَادُوٓاْ إِن زَعَمْتُمُۥٓ أَنَّكُمُۥٓ أَوْلِيَآءُ لِلهِ مِن دُونِ اِ۬لنَّاسِ فَتَمَنَّوُاْ اُ۬لْمَوْتَ إِن كُنتُمْ صَٰدِقِينَۖ ﰅ', '', NULL),
(5161, '', '', NULL, NULL, 28, 13, 12, 5161, 7, 553, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'وَلَا يَتَمَنَّوْنَهُۥٓ أَبَداَۢ بِمَا قَدَّمَتَ اَيْدِيهِمْۖ وَاللَّهُ عَلِيمُۢ بِالظَّٰلِمِينَۖ ﰆ', '', NULL);
INSERT INTO `ayahs` (`id`, `timecode_in`, `timecode_out`, `created_at`, `hizb_id`, `juz_id`, `line_end`, `line_start`, `number`, `number_in_surah`, `page`, `sajda`, `sura_name_ar`, `sura_name_en`, `surah_id`, `textdesc`, `textdesc_normalized`, `updated_at`) VALUES
(5162, '', '', NULL, NULL, 28, 15, 13, 5162, 8, 553, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'قُلِ اِنَّ اَ۬لْمَوْتَ اَ۬لذِے تَفِرُّونَ مِنْهُ فَإِنَّهُۥ مُلَٰقِيكُمْ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ اِ۬لْغَيْبِ وَالشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَۖ ﰇ', '', NULL),
(5163, '', '', NULL, NULL, 28, 2, 1, 5163, 9, 554, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُوٓاْ إِذَا نُودِيَ لِلصَّلَوٰةِ مِنْ يَّوْمِ اِ۬لْجُمُعَةِ فَاسْعَوِاْ اِلَىٰ ذِكْرِ اِ۬للَّهِ وَذَرُواْ اُ۬لْبَيْعَۖ ذَٰلِكُمْ خَيْرٞ لَّكُمُۥٓ إِن كُنتُمْ تَعْلَمُونَۖ ﰈ', '', NULL),
(5164, '', '', NULL, NULL, 28, 4, 3, 5164, 10, 554, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'فَإِذَا قُضِيَتِ اِ۬لصَّلَوٰةُ فَانتَشِرُواْ فِے اِ۬لَارْضِ وَابْتَغُواْ مِن فَضْلِ اِ۬للَّهِ وَاذْكُرُواْ اُ۬للَّهَ كَثِيراٗ لَّعَلَّكُمْ تُفْلِحُونَۖ ﰉ', '', NULL),
(5165, '', '', NULL, NULL, 28, 6, 4, 5165, 11, 554, NULL, 'الجُمعَة ', 'Al-Jumu‘ah', 62, 'وَإِذَا رَأَوْاْ تِجَٰرَةً اَوْ لَهْواً اِ۪نفَضُّوٓاْ إِلَيْهَا وَتَرَكُوكَ قَآئِماٗۖ قُلْ مَا عِندَ اَ۬للَّهِ خَيْرٞ مِّنَ اَ۬للَّهْوِ وَمِنَ اَ۬لتِّجَٰرَةِۖ وَاللَّهُ خَيْرُ اُ۬لرَّٰزِقِينَۖ ﰊ', '', NULL),
(5166, '', '', NULL, NULL, 28, 10, 9, 5166, 1, 554, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'إِذَا جَآءَكَ اَ۬لْمُنَٰفِقُونَ قَالُواْ نَشْهَدُ إِنَّكَ لَرَسُولُ اُ۬للَّهِۖ وَاللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُۥۖ وَاللَّهُ يَشْهَدُ إِنَّ اَ۬لْمُنَٰفِقِينَ لَكَٰذِبُونَۖ ﰀ', '', NULL),
(5167, '', '', NULL, NULL, 28, 11, 10, 5167, 2, 554, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'اَ۪تَّخَذُوٓاْ أَيْمَٰنَهُمْ جُنَّةٗ فَصَدُّواْ عَن سَبِيلِ اِ۬للَّهِۖ إِنَّهُمْ سَآءَ مَا كَانُواْ يَعْمَلُونَۖ ﰁ', '', NULL),
(5168, '', '', NULL, NULL, 28, 13, 12, 5168, 3, 554, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'ذَٰلِكَ بِأَنَّهُمُۥٓ ءَامَنُواْ ثُمَّ كَفَرُواْ فَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَۖ ﰂ', '', NULL),
(5169, '', '', NULL, NULL, 28, 15, 13, 5169, 4, 554, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'وَإِذَا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسَامُهُمْۖ وَإِنْ يَّقُولُواْ تَسْمَعْ لِقَوْلِهِمْ كَأَنَّهُمْ خُشُبٞ مُّسَنَّدَةٞۖ يَحْسِبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْۖ هُمُ اُ۬لْعَدُوُّۖ فَاحْذَرْهُمْۖ قَٰتَلَهُمُ اُ۬للَّهُۖ أَنّ۪ىٰ يُوفَكُونَۖ ﰃ', '', NULL),
(5170, '', '', NULL, NULL, 28, 2, 1, 5170, 5, 555, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'وَإِذَا قِيلَ لَهُمْ تَعَالَوْاْ يَسْتَغْفِرْ لَكُمْ رَسُولُ اُ۬للَّهِ لَوَوْاْ رُءُوسَهُمْ وَرَأَيْتَهُمْ يَصُدُّونَ وَهُم مُّسْتَكْبِرُونَۖ ﰄ', '', NULL),
(5171, '', '', NULL, NULL, 28, 4, 3, 5171, 6, 555, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'سَوَآءٌ عَلَيْهِمُۥٓ أَسْتَغْفَرْتَ لَهُمُۥٓ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْۖ لَنْ يَّغْفِرَ اَ۬للَّهُ لَهُمُۥٓۖ إِنَّ اَ۬للَّهَ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لْفَٰسِقِينَۖ ﰅ', '', NULL),
(5172, '', '', NULL, NULL, 28, 7, 5, 5172, 7, 555, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'هُمُ اُ۬لذِينَ يَقُولُونَ لَا تُنفِقُواْ عَلَىٰ مَنْ عِندَ رَسُولِ اِ۬للَّهِ حَتَّىٰ يَنفَضُّواْۖ وَلِلهِ خَزَآئِنُ اُ۬لسَّمَٰوَٰتِ وَالَارْضِۖ وَلَٰكِنَّ اَ۬لْمُنَٰفِقِينَ لَا يَفْقَهُونَۖ ﰆ', '', NULL),
(5173, '', '', NULL, NULL, 28, 9, 7, 5173, 8, 555, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'يَقُولُونَ لَئِن رَّجَعْنَآ إِلَى اَ۬لْمَدِينَةِ لَيُخْرِجَنَّ اَ۬لَاعَزُّ مِنْهَا اَ۬لَاذَلَّۖ وَلِلهِ اِ۬لْعِزَّةُ وَلِرَسُولِهِۦ وَلِلْمُومِنِينَۖ وَلَٰكِنَّ اَ۬لْمُنَٰفِقِينَ لَا يَعْلَمُونَۖ ﰇ', '', NULL),
(5174, '', '', NULL, NULL, 28, 12, 10, 5174, 9, 555, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, '۞ يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تُلْهِكُمُۥٓ أَمْوَٰلُكُمْ وَلَآ أَوْلَٰدُكُمْ عَن ذِكْرِ اِ۬للَّهِۖ وَمَنْ يَّفْعَلْ ذَٰلِكَ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْخَٰسِرُونَۖ ﰈ', '', NULL),
(5175, '', '', NULL, NULL, 28, 14, 12, 5175, 10, 555, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'وَأَنفِقُواْ مِن مَّا رَزَقْنَٰكُم مِّن قَبْلِ أَنْ يَّاتِيَ أَحَدَكُمُ اُ۬لْمَوْتُ فَيَقُولَ رَبِّ لَوْلَآ أَخَّرْتَنِےٓ إِلَىٰٓ أَجَلٖ قَرِيبٖ فَأَصَّدَّقَ وَأَكُن مِّنَ اَ۬لصَّٰلِحِينَۖ ﰉ', '', NULL),
(5176, '', '', NULL, NULL, 28, 15, 14, 5176, 11, 555, NULL, 'المُنَافِقُونَ ', 'Al-Munāfiqūn', 63, 'وَلَنْ يُّوَ۬خِّرَ اَ۬للَّهُ نَفْساً اِذَا جَآءَ اجَلُهَاۖ وَاللَّهُ خَبِيرُۢ بِمَا تَعْمَلُونَۖ ﰊ', '', NULL),
(5177, '', '', NULL, NULL, 28, 4, 3, 5177, 1, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'يُسَبِّحُ لِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لَارْضِۖ لَهُ اُ۬لْمُلْكُ وَلَهُ اُ۬لْحَمْدُۖ وَهُوَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ﰀ', '', NULL),
(5178, '', '', NULL, NULL, 28, 5, 4, 5178, 2, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'هُوَ اَ۬لذِے خَلَقَكُمْ فَمِنكُمْ كَافِرٞ وَمِنكُم مُّومِنٞۖ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌۖ ﰁ', '', NULL),
(5179, '', '', NULL, NULL, 28, 6, 5, 5179, 3, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالَارْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْۖ وَإِلَيْهِ اِ۬لْمَصِيرُۖ ﰂ', '', NULL),
(5180, '', '', NULL, NULL, 28, 7, 6, 5180, 4, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'يَعْلَمُ مَا فِے اِ۬لسَّمَٰوَٰتِ وَالَارْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَۖ وَاللَّهُ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِۖ ﰃ', '', NULL),
(5181, '', '', NULL, NULL, 28, 9, 7, 5181, 5, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'أَلَمْ يَاتِكُمْ نَبَؤُاْ اُ۬لذِينَ كَفَرُواْ مِن قَبْلُ فَذَاقُواْ وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ اَلِيمٞۖ ﰄ', '', NULL),
(5182, '', '', NULL, NULL, 28, 10, 9, 5182, 6, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'ذَٰلِكَ بِأَنَّهُۥ كَانَت تَّاتِيهِمْ رُسُلُهُم بِالْبَيِّنَٰتِ فَقَالُوٓاْ أَبَشَرٞ يَهْدُونَنَاۖ فَكَفَرُواْ وَتَوَلَّواْ وَّاسْتَغْنَى اَ۬للَّهُۖ وَاللَّهُ غَنِيٌّ حَمِيدٞۖ ﰅ', '', NULL),
(5183, '', '', NULL, NULL, 28, 12, 10, 5183, 7, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, '۞ زَعَمَ اَ۬لذِينَ كَفَرُوٓاْ أَن لَّنْ يُّبْعَثُواْۖ قُلْ بَل۪ىٰ وَرَبِّے لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْۖ وَذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيرٞۖ ﰆ', '', NULL),
(5184, '', '', NULL, NULL, 28, 13, 12, 5184, 8, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'فَـَٔامِنُواْ بِاللَّهِ وَرَسُولِهِۦ وَالنُّورِ اِ۬لذِےٓ أَنزَلْنَاۖ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٞۖ ﰇ', '', NULL),
(5185, '', '', NULL, NULL, 28, 15, 13, 5185, 9, 556, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ اِ۬لْجَمْعِ ذَٰلِكَ يَوْمُ اُ۬لتَّغَابُنِۖ وَمَنْ يُّومِنۢ بِاللَّهِ وَيَعْمَلْ صَٰلِحاٗ نُّكَفِّرْ عَنْهُ سَيِّـَٔاتِهِۦ وَنُدْخِلْهُ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لَانْهَٰرُ خَٰلِدِينَ فِيهَآ أَبَداٗۖ ذَٰلِكَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ﰈ', '', NULL),
(5186, '', '', NULL, NULL, 28, 2, 1, 5186, 10, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'وَالذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنّ۪ارِ خَٰلِدِينَ فِيهَاۖ وَبِيسَ اَ۬لْمَصِيرُۖ ﰉ', '', NULL),
(5187, '', '', NULL, NULL, 28, 4, 3, 5187, 11, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'مَآ أَصَابَ مِن مُّصِيبَةٍ اِلَّا بِإِذْنِ اِ۬للَّهِۖ وَمَنْ يُّومِنۢ بِاللَّهِ يَهْدِ قَلْبَهُۥۖ وَاللَّهُ بِكُلِّ شَےْءٍ عَلِيمٞۖ ﰊ', '', NULL),
(5188, '', '', NULL, NULL, 28, 6, 4, 5188, 12, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'وَأَطِيعُواْ اُ۬للَّهَ وَأَطِيعُواْ اُ۬لرَّسُولَۖ فَإِن تَوَلَّيْتُمْ فَإِنَّمَا عَلَىٰ رَسُولِنَا اَ۬لْبَلَٰغُ اُ۬لْمُبِينُۖ ﰋ', '', NULL),
(5189, '', '', NULL, NULL, 28, 7, 6, 5189, 13, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'اُ۬للَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ وَعَلَى اَ۬للَّهِ فَلْيَتَوَكَّلِ اِ۬لْمُومِنُونَۖ ﰌ', '', NULL),
(5190, '', '', NULL, NULL, 28, 9, 7, 5190, 14, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُوٓاْ إِنَّ مِنَ اَزْوَٰجِكُمْ وَأَوْلَٰدِكُمْ عَدُوّاٗ لَّكُمْ فَاحْذَرُوهُمْۖ وَإِن تَعْفُواْ وَتَصْفَحُواْ وَتَغْفِرُواْ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌۖ ﰍ', '', NULL),
(5191, '', '', NULL, NULL, 28, 11, 9, 5191, 15, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'اِنَّمَآ أَمْوَٰلُكُمْ وَأَوْلَٰدُكُمْ فِتْنَةٞۖ وَاللَّهُ عِندَهُۥٓ أَجْرٌ عَظِيمٞۖ ﰎ', '', NULL),
(5192, '', '', NULL, NULL, 28, 13, 11, 5192, 16, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'فَاتَّقُواْ اُ۬للَّهَ مَا اَ۪سْتَطَعْتُمْ وَاسْمَعُواْ وَأَطِيعُواْ وَأَنفِقُواْ خَيْراٗ لِّأَنفُسِكُمْۖ وَمَنْ يُّوقَ شُحَّ نَفْسِهِۦ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُفْلِحُونَۖ ﰏ', '', NULL),
(5193, '', '', NULL, NULL, 28, 15, 13, 5193, 17, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'إِن تُقْرِضُواْ اُ۬للَّهَ قَرْضاً حَسَناٗ يُضَٰعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْۖ وَاللَّهُ شَكُورٌ حَلِيمٌۖ ﰐ', '', NULL),
(5194, '', '', NULL, NULL, 28, 15, 15, 5194, 18, 557, NULL, 'التغَابُن ', 'At-Taghābun', 64, 'عَٰلِمُ اُ۬لْغَيْبِ وَالشَّهَٰدَةِۖ اِ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ﰑ', '', NULL),
(5195, '', '', NULL, NULL, 28, 6, 3, 5195, 1, 558, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'يَٰٓأَيُّهَا‏ اَ۬لنَّبِےٓءُ اِ۪ذَا طَلَّقْتُمُ اُ۬لنِّسَآءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحْصُواْ اُ۬لْعِدَّةَۖ وَاتَّقُواْ اُ۬للَّهَ رَبَّكُمْۖ لَا تُخْرِجُوهُنَّ مِنۢ بُيُوتِهِنَّۖ وَلَا يَخْرُجْنَ إِلَّآ أَنْ يَّاتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۖ وَتِلْكَ حُدُودُ اُ۬للَّهِۖ وَمَنْ يَّتَعَدَّ حُدُودَ اَ۬للَّهِ فَقَد ظَّلَمَ نَفْسَهُۥۖ لَا تَدْرِے لَعَلَّ اَ۬للَّهَ يُحْدِثُ بَعْدَ ذَٰلِكَ أَمْراٗۖ ﰀ', '', NULL),
(5196, '', '', NULL, NULL, 28, 9, 6, 5196, 2, 558, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ اَوْ فَارِقُوهُنَّ بِمَعْرُوفٖۖ وَأَشْهِدُواْ ذَوَےْ عَدْلٖ مِّنكُمْۖ وَأَقِيمُواْ اُ۬لشَّهَٰدَةَ لِلهِۖ ذَٰلِكُمْ يُوعَظُ بِهِۦ مَن كَانَ يُومِنُ بِاللَّهِ وَالْيَوْمِ اِ۬لَاخِرِۖ وَمَنْ يَّتَّقِ اِ۬للَّهَ يَجْعَل لَّهُۥ مَخْرَجاٗ ﰁ', '', NULL),
(5197, '', '', NULL, NULL, 28, 11, 10, 5197, 3, 558, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُۖ وَمَنْ يَّتَوَكَّلْ عَلَى اَ۬للَّهِ فَهُوَ حَسْبُهُۥٓۖ إِنَّ اَ۬للَّهَ بَٰلِغٌ اَمْرَهُۥۖ قَدْ جَعَلَ اَ۬للَّهُ لِكُلِّ شَےْءٖ قَدْراٗۖ ﰂ', '', NULL),
(5198, '', '', NULL, NULL, 28, 14, 11, 5198, 4, 558, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'وَال۪ےْ يَئِسْنَ مِنَ اَ۬لْمَحِيضِ مِن نِّسَآئِكُمُۥٓ إِنِ اِ۪رْتَبْتُمْ فَعِدَّتُهُنَّ ثَلَٰثَةُ أَشْهُرٖ وَال۪ےْ لَمْ يَحِضْنَۖ وَأُوْلَٰتُ اُ۬لَاحْمَالِ أَجَلُهُنَّ أَنْ يَّضَعْنَ حَمْلَهُنَّۖ وَمَنْ يَّتَّقِ اِ۬للَّهَ يَجْعَل لَّهُۥ مِنَ اَمْرِهِۦ يُسْراٗۖ ﰃ', '', NULL),
(5199, '', '', NULL, NULL, 28, 15, 14, 5199, 5, 558, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'ذَٰلِكَ أَمْرُ اُ۬للَّهِ أَنزَلَهُۥٓ إِلَيْكُمْۖ وَمَنْ يَّتَّقِ اِ۬للَّهَ يُكَفِّرْ عَنْهُ سَيِّـَٔاتِهِۦ وَيُعْظِمْ لَهُۥٓ أَجْراًۖ ﰄ', '', NULL),
(5200, '', '', NULL, NULL, 28, 4, 1, 5200, 6, 559, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'اَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنتُم مِّنْ وُّجْدِكُمْۖ وَلَا تُضَآرُّوهُنَّ لِتُضَيِّقُواْ عَلَيْهِنَّۖ وَإِن كُنَّ أُوْلَٰتِ حَمْلٖ فَأَنفِقُواْ عَلَيْهِنَّ حَتَّىٰ يَضَعْنَ حَمْلَهُنَّۖ فَإِنَ اَرْضَعْنَ لَكُمْ فَـَٔاتُوهُنَّ أُجُورَهُنَّ وَاتَمِرُواْ بَيْنَكُم بِمَعْرُوفٖۖ ۞ وَإِن تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُۥٓ أُخْر۪ىٰۖ ﰅ', '', NULL),
(5201, '', '', NULL, NULL, 28, 6, 4, 5201, 7, 559, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'لِيُنفِقْ ذُو سَعَةٖ مِّن سَعَتِهِۦۖ وَمَن قُدِرَ عَلَيْهِ رِزْقُهُۥ فَلْيُنفِقْ مِمَّآ ءَات۪يٰهُ اُ۬للَّهُۖ لَا يُكَلِّفُ اُ۬للَّهُ نَفْساً اِلَّا مَآ ءَات۪يٰهَاۖ سَيَجْعَلُ اُ۬للَّهُ بَعْدَ عُسْرٖ يُسْراٗۖ ﰆ', '', NULL),
(5202, '', '', NULL, NULL, 28, 8, 6, 5202, 8, 559, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'وَكَأَيِّن مِّن قَرْيَةٍ عَتَتْ عَنَ اَمْرِ رَبِّهَا وَرُسُلِهِۦ فَحَاسَبْنَٰهَا حِسَاباٗ شَدِيداٗ وَعَذَّبْنَٰهَا عَذَاباٗ نُّكُراٗ ﰇ', '', NULL),
(5203, '', '', NULL, NULL, 28, 8, 8, 5203, 9, 559, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'فَذَاقَتْ وَبَالَ أَمْرِهَاۖ وَكَانَ عَٰقِبَةُ أَمْرِهَا خُسْراًۖ ﰈ', '', NULL),
(5204, '', '', NULL, NULL, 28, 10, 8, 5204, 10, 559, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'اَعَدَّ اَ۬للَّهُ لَهُمْ عَذَاباٗ شَدِيداٗۖ فَاتَّقُواْ اُ۬للَّهَ يَٰٓأُوْلِے اِ۬لَالْبَٰبِۖ اِ۬لذِينَ ءَامَنُواْ قَدَ اَنزَلَ اَ۬للَّهُ إِلَيْكُمْ ذِكْراٗۖ ﰉ', '', NULL),
(5205, '', '', NULL, NULL, 28, 13, 10, 5205, 11, 559, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'رَّسُولاٗ يَتْلُواْ عَلَيْكُمُۥٓ ءَايَٰتِ اِ۬للَّهِ مُبَيَّنَٰتٖ لِّيُخْرِجَ اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ مِنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِۖ وَمَنْ يُّومِنۢ بِاللَّهِ وَيَعْمَلْ صَٰلِحاٗ نُّدْخِلْهُ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لَانْهَٰرُ خَٰلِدِينَ فِيهَآ أَبَداٗ قَدَ اَحْسَنَ اَ۬للَّهُ لَهُۥ رِزْقاًۖ ﰊ', '', NULL),
(5206, '', '', NULL, NULL, 28, 15, 13, 5206, 12, 559, NULL, 'الطَّلَاق ', 'Aṭ-Ṭalāq', 65, 'اِ۬للَّهُ اُ۬لذِے خَلَقَ سَبْعَ سَمَٰوَٰتٖ وَمِنَ اَ۬لَارْضِ مِثْلَهُنَّۖ يَتَنَزَّلُ اُ۬لَامْرُ بَيْنَهُنَّ لِتَعْلَمُوٓاْ أَنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞ وَأَنَّ اَ۬للَّهَ قَدَ اَحَاطَ بِكُلِّ شَےْءٍ عِلْماٗۖ ﰋ', '', NULL),
(5207, '', '', NULL, NULL, 28, 4, 3, 5207, 1, 560, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'يَٰٓأَيُّهَا‏ اَ۬لنَّبِےٓءُ لِمَ تُحَرِّمُ مَآ أَحَلَّ اَ۬للَّهُ لَكَ تَبْتَغِے مَرْضَاتَ أَزْوَٰجِكَۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ﰀ', '', NULL),
(5208, '', '', NULL, NULL, 28, 5, 4, 5208, 2, 560, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'قَدْ فَرَضَ اَ۬للَّهُ لَكُمْ تَحِلَّةَ أَيْمَٰنِكُمْۖ وَاللَّهُ مَوْل۪يٰكُمْۖ وَهُوَ اَ۬لْعَلِيمُ اُ۬لْحَكِيمُۖ ﰁ', '', NULL),
(5209, '', '', NULL, NULL, 28, 8, 5, 5209, 3, 560, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'وَإِذَ اَسَرَّ‏ اَ۬لنَّبِےٓءُ اِ۪لَىٰ بَعْضِ أَزْوَٰجِهِۦ حَدِيثاٗۖ فَلَمَّا نَبَّأَتْ بِهِۦ وَأَظْهَرَهُ اُ۬للَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُۥ وَأَعْرَضَ عَنۢ بَعْضٖۖ فَلَمَّا نَبَّأَهَا بِهِۦ قَالَتْ مَنَ اَنۢبَأَكَ هَٰذَا قَالَ نَبَّأَنِيَ اَ۬لْعَلِيمُ اُ۬لْخَبِيرُۖ ﰂ', '', NULL),
(5210, '', '', NULL, NULL, 28, 10, 8, 5210, 4, 560, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'إِن تَتُوبَآ إِلَى اَ۬للَّهِ فَقَدْ صَغَتْ قُلُوبُكُمَاۖ وَإِن تَظَّٰهَرَا عَلَيْهِ فَإِنَّ اَ۬للَّهَ هُوَ مَوْل۪يٰهُ وَجِبْرِيلُ وَصَٰلِحُ اُ۬لْمُومِنِينَۖ وَالْمَلَٰٓئِكَةُ بَعْدَ ذَٰلِكَ ظَهِيرٌۖ ﰃ', '', NULL),
(5211, '', '', NULL, NULL, 28, 12, 10, 5211, 5, 560, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'عَس۪ىٰ رَبُّهُۥٓ إِن طَلَّقَكُنَّ أَنْ يُّبَدِّلَهُۥٓ أَزْوَٰجاً خَيْراٗ مِّنكُنَّ مُسْلِمَٰتٖ مُّومِنَٰتٖ قَٰنِتَٰتٖ تَٰٓئِبَٰتٍ عَٰبِدَٰتٖ سَٰٓئِحَٰتٖ ثَيِّبَٰتٖ وَأَبْكَاراٗۖ ﰄ', '', NULL),
(5212, '', '', NULL, NULL, 28, 14, 12, 5212, 6, 560, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ قُوٓاْ أَنفُسَكُمْ وَأَهْلِيكُمْ نَاراٗ وَقُودُهَا اَ۬لنَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلَٰٓئِكَةٌ غِلَٰظٞ شِدَادٞ لَّا يَعْصُونَ اَ۬للَّهَ مَآ أَمَرَهُمْ وَيَفْعَلُونَ مَا يُومَرُونَۖ ﰅ', '', NULL),
(5213, '', '', NULL, NULL, 28, 15, 14, 5213, 7, 560, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'يَٰٓأَيُّهَا اَ۬لذِينَ كَفَرُواْ لَا تَعْتَذِرُواْ اُ۬لْيَوْمَ إِنَّمَا تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَۖ ﰆ', '', NULL),
(5214, '', '', NULL, NULL, 28, 5, 1, 5214, 8, 561, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, '۞ يَٰٓأَيُّهَا اَ۬لذِينَ ءَامَنُواْ تُوبُوٓاْ إِلَى اَ۬للَّهِ تَوْبَةٗ نَّصُوحاً عَس۪ىٰ رَبُّكُمُۥٓ أَنْ يُّكَفِّرَ عَنكُمْ سَيِّـَٔاتِكُمْ وَيُدْخِلَكُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لَانْهَٰرُ يَوْمَ لَا يُخْزِے اِ۬للَّهُ اُ۬لنَّبِےٓءَ وَالذِينَ ءَامَنُواْ مَعَهُۥ نُورُهُمْ يَسْع۪ىٰ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَٰنِهِمْۖ يَقُولُونَ رَبَّنَآ أَتْمِمْ لَنَا نُورَنَا وَاغْفِرْ لَنَآۖ إِنَّكَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ﰇ', '', NULL),
(5215, '', '', NULL, NULL, 28, 7, 6, 5215, 9, 561, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'يَٰٓأَيُّهَا‏ اَ۬لنَّبِےٓءُ جَٰهِدِ اِ۬لْكُفَّارَ وَالْمُنَٰفِقِينَ وَاغْلُظْ عَلَيْهِمْۖ وَمَأْو۪يٰهُمْ جَهَنَّمُۖ وَبِيسَ اَ۬لْمَصِيرُۖ ﰈ', '', NULL),
(5216, '', '', NULL, NULL, 28, 10, 7, 5216, 10, 561, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'ضَرَبَ اَ۬للَّهُ مَثَلاٗ لِّلذِينَ كَفَرُواْ اُ۪مْرَأَتَ نُوحٖ وَامْرَأَتَ لُوطٖۖ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتَٰهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اَ۬للَّهِ شَئْاٗۖ وَقِيلَ اَ۟دْخُلَا اَ۬لنَّارَ مَعَ اَ۬لدَّٰخِلِينَۖ ﰉ', '', NULL),
(5217, '', '', NULL, NULL, 28, 13, 11, 5217, 11, 561, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'وَضَرَبَ اَ۬للَّهُ مَثَلاٗ لِّلذِينَ ءَامَنُواْ اُ۪مْرَأَتَ فِرْعَوْنَ إِذْ قَالَتْ رَبِّ اِ۪بْنِ لِے عِندَكَ بَيْتاٗ فِے اِ۬لْجَنَّةِ وَنَجِّنِے مِن فِرْعَوْنَ وَعَمَلِهِۦ وَنَجِّنِے مِنَ اَ۬لْقَوْمِ اِ۬لظَّٰلِمِينَۖ ﰊ', '', NULL),
(5218, '', '', NULL, NULL, 28, 15, 13, 5218, 12, 561, NULL, 'التَّحرِيم ', 'At-Taḥrīm', 66, 'وَمَرْيَمَ اَ۪بْنَتَ عِمْرَٰنَ اَ۬لتِےٓ أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهِ مِن رُّوحِنَا وَصَدَّقَتْ بِكَلِمَٰتِ رَبِّهَا وَكِتَٰبِهِۦ وَكَانَتْ مِنَ اَ۬لْقَٰنِتِينَۖ ﰋ', '', NULL),
(5219, '', '', NULL, NULL, 29, 3, 3, 5219, 1, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'تَبَٰرَكَ اَ۬لذِے بِيَدِهِ اِ۬لْمُلْكُ وَهُوَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ﰀ', '', NULL),
(5220, '', '', NULL, NULL, 29, 4, 3, 5220, 2, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'اِ۬لذِے خَلَقَ اَ۬لْمَوْتَ وَالْحَيَوٰةَ لِيَبْلُوَكُمُۥٓ أَيُّكُمُۥٓ أَحْسَنُ عَمَلاٗۖ وَهُوَ اَ۬لْعَزِيزُ اُ۬لْغَفُورُۖ ﰁ', '', NULL),
(5221, '', '', NULL, NULL, 29, 6, 5, 5221, 3, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'اُ۬لذِے خَلَقَ سَبْعَ سَمَٰوَٰتٖ طِبَاقاٗۖ مَّا تَر۪ىٰ فِے خَلْقِ اِ۬لرَّحْمَٰنِ مِن تَفَٰوُتٖۖ فَارْجِعِ اِ۬لْبَصَرَ هَلْ تَر۪ىٰ مِن فُطُورٖۖ ﰂ', '', NULL),
(5222, '', '', NULL, NULL, 29, 7, 6, 5222, 4, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'ثُمَّ اَ۪رْجِعِ اِ۬لْبَصَرَ كَرَّتَيْنِ يَنقَلِبِ اِلَيْكَ اَ۬لْبَصَرُ خَاسِئاٗ وَهُوَ حَسِيرٞۖ ﰃ', '', NULL),
(5223, '', '', NULL, NULL, 29, 9, 7, 5223, 5, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'وَلَقَدْ زَيَّنَّا اَ۬لسَّمَآءَ اَ۬لدُّنْي۪ا بِمَصَٰبِيحَ وَجَعَلْنَٰهَا رُجُوماٗ لِّلشَّيَٰطِينِ وَأَعْتَدْنَا لَهُمْ عَذَابَ اَ۬لسَّعِيرِۖ ﰄ', '', NULL),
(5224, '', '', NULL, NULL, 29, 9, 9, 5224, 6, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'وَلِلذِينَ كَفَرُواْ بِرَبِّهِمْ عَذَابُ جَهَنَّمَۖ وَبِيسَ اَ۬لْمَصِيرُۖ ﰅ', '', NULL),
(5225, '', '', NULL, NULL, 29, 10, 10, 5225, 7, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'إِذَآ أُلْقُواْ فِيهَا سَمِعُواْ لَهَا شَهِيقاٗ وَهِيَ تَفُورُ ﰆ', '', NULL),
(5226, '', '', NULL, NULL, 29, 11, 10, 5226, 8, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'تَكَادُ تَمَيَّزُ مِنَ اَ۬لْغَيْظِۖ كُلَّمَآ أُلْقِيَ فِيهَا فَوْجٞ سَأَلَهُمْ خَزَنَتُهَآ أَلَمْ يَاتِكُمْ نَذِيرٞۖ ﰇ', '', NULL),
(5227, '', '', NULL, NULL, 29, 12, 11, 5227, 9, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'قَالُواْ بَل۪ىٰ قَدْ جَآءَنَا نَذِيرٞ ﰈ', '', NULL),
(5228, '', '', NULL, NULL, 29, 13, 12, 5228, 10, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اَ۬للَّهُ مِن شَےْءٍ اِنَ اَنتُمُۥٓ إِلَّا فِے ضَلَٰلٖ كَبِيرٖۖ ﰉ', '', NULL),
(5229, '', '', NULL, NULL, 29, 14, 13, 5229, 11, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'وَقَالُواْ لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ مَا كُنَّا فِےٓ أَصْحَٰبِ اِ۬لسَّعِيرِۖ ﰊ', '', NULL),
(5230, '', '', NULL, NULL, 29, 14, 14, 5230, 12, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'فَاعْتَرَفُواْ بِذَنۢبِهِمْۖ فَسُحْقاٗ لِّأَصْحَٰبِ اِ۬لسَّعِيرِۖ ﰋ', '', NULL),
(5231, '', '', NULL, NULL, 29, 15, 14, 5231, 13, 562, NULL, 'المُلك ', 'Al-Mulk', 67, 'إِنَّ اَ۬لذِينَ يَخْشَوْنَ رَبَّهُم بِالْغَيْبِ لَهُم مَّغْفِرَةٞ وَأَجْرٞ كَبِيرٞۖ ﰌ', '', NULL),
(5232, '', '', NULL, NULL, 29, 1, 1, 5232, 14, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'وَأَسِرُّواْ قَوْلَكُمُۥٓ أَوِ اِ۪جْهَرُواْ بِهِۦٓۖ إِنَّهُۥ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِۖ ﰍ', '', NULL),
(5233, '', '', NULL, NULL, 29, 2, 1, 5233, 15, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'أَلَا يَعْلَمُ مَنْ خَلَقَ وَهُوَ اَ۬للَّطِيفُ اُ۬لْخَبِيرُۖ ﰎ', '', NULL),
(5234, '', '', NULL, NULL, 29, 3, 2, 5234, 16, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'هُوَ اَ۬لذِے جَعَلَ لَكُمُ اُ۬لَارْضَ ذَلُولاٗ فَامْشُواْ فِے مَنَاكِبِهَا وَكُلُواْ مِن رِّزْقِهِۦۖ وَإِلَيْهِ اِ۬لنُّشُورُۖ ﰏ', '', NULL),
(5235, '', '', NULL, NULL, 29, 4, 4, 5235, 17, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'ءَامِنتُم مَّن فِے اِ۬لسَّمَآءِ اَ۬نْ يَّخْسِفَ بِكُمُ اُ۬لَارْضَ فَإِذَا هِيَ تَمُورُۖ ﰐ', '', NULL),
(5236, '', '', NULL, NULL, 29, 6, 5, 5236, 18, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'أَمَ اَمِنتُم مَّن فِے اِ۬لسَّمَآءِ اَ۬نْ يُّرْسِلَ عَلَيْكُمْ حَاصِباٗۖ فَسَتَعْلَمُونَ كَيْفَ نَذِيرِۦۖ ﰑ', '', NULL),
(5237, '', '', NULL, NULL, 29, 6, 6, 5237, 19, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'وَلَقَدْ كَذَّبَ اَ۬لذِينَ مِن قَبْلِهِمْۖ فَكَيْفَ كَانَ نَكِيرِۦٓۖ ﰒ', '', NULL),
(5238, '', '', NULL, NULL, 29, 8, 7, 5238, 20, 563, NULL, 'المُلك ', 'Al-Mulk', 67, '۞ أَوَلَمْ يَرَوِاْ اِلَى اَ۬لطَّيْرِ فَوْقَهُمْ صَٰٓفَّٰتٖ وَيَقْبِضْنَۖ مَا يُمْسِكُهُنَّ إِلَّا اَ۬لرَّحْمَٰنُۖ إِنَّهُۥ بِكُلِّ شَےْءِۢ بَصِيرٌۖ ﰓ', '', NULL),
(5239, '', '', NULL, NULL, 29, 9, 8, 5239, 21, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'اَمَّنْ هَٰذَا اَ۬لذِے هُوَ جُندٞ لَّكُمْ يَنصُرُكُم مِّن دُونِ اِ۬لرَّحْمَٰنِۖ إِنِ اِ۬لْكَٰفِرُونَ إِلَّا فِے غُرُورٍۖ ﰔ', '', NULL),
(5240, '', '', NULL, NULL, 29, 10, 9, 5240, 22, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'اَمَّنْ هَٰذَا اَ۬لذِے يَرْزُقُكُمُۥٓ إِنَ اَمْسَكَ رِزْقَهُۥۖ بَل لَّجُّواْ فِے عُتُوّٖ وَنُفُورٍۖ ﰕ', '', NULL),
(5241, '', '', NULL, NULL, 29, 12, 10, 5241, 23, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'اَفَمَنْ يَّمْشِے مُكِبّاً عَلَىٰ وَجْهِهِۦٓ أَهْد۪ىٰٓ أَمَّنْ يَّمْشِے سَوِيّاً عَلَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ﰖ', '', NULL),
(5242, '', '', NULL, NULL, 29, 13, 12, 5242, 24, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'قُلْ هُوَ اَ۬لذِےٓ أَنشَأَكُمْ وَجَعَلَ لَكُمُ اُ۬لسَّمْعَ وَالَابْصَٰرَ وَالَافْـِٕدَةَۖ قَلِيلاٗ مَّا تَشْكُرُونَۖ ﰗ', '', NULL),
(5243, '', '', NULL, NULL, 29, 14, 13, 5243, 25, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'قُلْ هُوَ اَ۬لذِے ذَرَأَكُمْ فِے اِ۬لَارْضِ وَإِلَيْهِ تُحْشَرُونَۖ ﰘ', '', NULL),
(5244, '', '', NULL, NULL, 29, 15, 14, 5244, 26, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'وَيَقُولُونَ مَت۪ىٰ هَٰذَا اَ۬لْوَعْدُ إِن كُنتُمْ صَٰدِقِينَۖ ﰙ', '', NULL),
(5245, '', '', NULL, NULL, 29, 15, 15, 5245, 27, 563, NULL, 'المُلك ', 'Al-Mulk', 67, 'قُلِ اِنَّمَا اَ۬لْعِلْمُ عِندَ اَ۬للَّهِ وَإِنَّمَآ أَنَا نَذِيرٞ مُّبِينٞۖ ﰚ', '', NULL),
(5246, '', '', NULL, NULL, 29, 2, 1, 5246, 28, 564, NULL, 'المُلك ', 'Al-Mulk', 67, 'فَلَمَّا رَأَوْهُ زُلْفَةٗ س۬يَٓٔتْ وُجُوهُ اُ۬لذِينَ كَفَرُواْ وَقِيلَ هَٰذَا اَ۬لذِے كُنتُم بِهِۦ تَدَّعُونَۖ ﰛ', '', NULL),
(5247, '', '', NULL, NULL, 29, 3, 2, 5247, 29, 564, NULL, 'المُلك ', 'Al-Mulk', 67, 'قُلَ اَرَٰٓيْتُمُۥٓ إِنَ اَهْلَكَنِيَ اَ۬للَّهُ وَمَن مَّعِيَ أَوْ رَحِمَنَا فَمَنْ يُّجِيرُ اُ۬لْكٰ۪فِرِينَ مِنْ عَذَابٍ اَلِيمٖۖ ﰜ', '', NULL),
(5248, '', '', NULL, NULL, 29, 4, 3, 5248, 30, 564, NULL, 'المُلك ', 'Al-Mulk', 67, 'قُلْ هُوَ اَ۬لرَّحْمَٰنُ ءَامَنَّا بِهِۦ وَعَلَيْهِ تَوَكَّلْنَاۖ فَسَتَعْلَمُونَ مَنْ هُوَ فِے ضَلَٰلٖ مُّبِينٖۖ ﰝ', '', NULL),
